select 'P' Tipo, fac_emp_cod, fac_dep_cod, fac_Seq, fac_sef_cod, fac_dia, fac_tot, fac_num, fac_emldia, fac_emlpen, 
   fac_emlrei, FAC_DATEMLPEN, nvl(fac_emlfac, grf_eml) grf_eml, 
   fad_parametre ('PATHPDF')  || '\' || fac_emp_cod || '_' || fac_dep_cod || '_' || fac_sef_cod  || '_' || fac_num || '.pdf' fitxer, 
   EMP_RAZSOC emp, fac_cli_cod, cli_razsoc, fac_emlcnf
   from tad_factu, tad_grfac, tad_empre, tad_clien
   where GRF_EMP_COD = fac_emp_cod and GRF_DEP_COD = fac_dep_cod and GRF_CLI_COD = fac_cli_cod and cli_cod = fac_cli_cod
   and GRF_COD = fac_grf_cod and grf_eml is not null 
   and emp_cod = fac_emp_cod
   and (nvl(fac_emlpen, 'N') = 'S') and fac_emlrei is null
   union all
   select 'E' Tipo, fac_emp_cod, fac_dep_cod, fac_Seq, fac_sef_cod, fac_dia, fac_tot, fac_num, fac_emldia, fac_emlpen, fac_emlrei, FAC_DATEMLPEN, nvl(fac_emlfac, grf_eml) grf_eml, 
   fad_parametre ('PATHPDF')  || '\' || fac_emp_cod || '_' || fac_dep_cod || '_' || fac_sef_cod  || '_' || fac_num || '.pdf' fitxer, EMP_RAZSOC emp, fac_cli_cod, cli_razsoc, fac_emlcnf
   from tad_factu, tad_grfac, tad_empre, tad_clien
   where GRF_EMP_COD = fac_emp_cod and GRF_DEP_COD = fac_dep_cod and GRF_CLI_COD = fac_cli_cod and cli_cod = fac_cli_cod
   and GRF_COD = fac_grf_cod /*and nvl(GRF_ENVEML, 'N') = 'S'*/ 
   and emp_cod = fac_emp_cod    
   and nvl(fac_emlpen, 'N') = 'N' and FAC_EMLDIA is not null 
   union all
   select 'I' Tipo, fac_emp_cod, fac_dep_cod, fac_Seq, fac_sef_cod, fac_dia, fac_tot, fac_num, fac_emldia, fac_emlpen, fac_emlrei, FAC_DATEMLPEN, nvl(fac_emlfac, grf_eml) grf_eml, 
   fad_parametre ('PATHPDF')  || '\' || fac_emp_cod || '_' || fac_dep_cod || '_' || fac_sef_cod  || '_' || fac_num || '.pdf' fitxer, EMP_RAZSOC emp, fac_cli_cod, cli_razsoc, fac_emlcnf
   from tad_factu, tad_grfac, tad_empre, tad_clien
   where GRF_EMP_COD = fac_emp_cod and GRF_DEP_COD = fac_dep_cod and GRF_CLI_COD = fac_cli_cod and cli_cod = fac_cli_cod
   and GRF_COD = fac_grf_cod and grf_eml is not null and nvl(GRF_ENVEML, 'N') = 'S' 
   and emp_cod = fac_emp_cod    
   and FAC_EMLDIA is null and fac_emlrei is not null;


CREATE OR REPLACE FORCE VIEW VGES_ACF
AS 
SELECT
ACF_COD, ACF_IDF_COD, ACF_TIP, ACF_DAT, ACF_PRJ_NUM,
ACF_PRE_COD, ACF_CAP_COD, ACF_PDA_COD, ACF_ART_COD, ACF_UNI,
ACF_IMP, ACF_UNI001, ACF_IMP001, ACF_UNI002, ACF_IMP002, ACF_UNI003,
ACF_IMP003, ACF_CLI_COD, ACF_EMP_COD,
--decode(acf_tip, 'M', acf_uni + acf_uni001 + acf_uni002 , acf_uni) acf_uni_NN,
acf_uni acf_uni_NN,
decode(acf_tip, 'M', acf_uni + acf_uni003, acf_uni + acf_uni001 ) acf_uni_NS,
decode(acf_tip, 'M', acf_uni + acf_uni002, acf_uni)               acf_uni_SN,
decode(acf_tip, 'M', acf_uni + acf_uni003 /* ? */, acf_uni + acf_uni001 ) acf_uni_SS,
---
decode(acf_tip, 'M', acf_imp + acf_imp001 + acf_imp002 , acf_imp) acf_imp_NN,
decode(acf_tip, 'M', acf_imp + acf_imp003, acf_imp + acf_imp001 ) acf_imp_NS,
decode(acf_tip, 'M', acf_imp + acf_imp002, acf_imp) acf_imp_SN,
decode(acf_tip, 'M', acf_imp + acf_imp003 -
                     ((acf_uni001 + acf_uni003)*
                     (acf_imp001/decode(acf_uni001, 0 , 1 , acf_uni001)) *
                       LEAST(acf_uni001 - acf_uni002 , 1 )), acf_imp + acf_imp001 ) acf_imp_SS
FROM
tges_acf;


