CREATE OR REPLACE FORCE VIEW V1
AS 
SELECT ALL pre_idf_cod, pre_emp_cod, lpr_pre_cod pre_cod, pre_cli_cod, pre_mag_cod, 
  lpr_art_cod lpr_art_cod, to_number(null) lpr_num, lpr_diaminini, 
  sum( lpr_uni ) dsp_uni, 
  art_des dsp_des , art_tipuni, lpr_unisto, nvl(lpr_cap_cod, 'zz*/') lpr_cap_cod, nvl(lpr_pda_cod, 'zz*/') lpr_pda_cod 
from tges_lpr,tges_art,tges_pre 
where               lpr_art_cod = art_cod and lpr_idf_cod = art_idf_cod 
	  and lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and lpr_pre_cod = pre_cod 
	  and nvl( art_grpfac, 'N') = 'S' 
                  and ( (pre_est='A' and lpr_acc='S') OR pre_est <>'A') 
                  /* No tomar los artculos que tienen escandallo */ 
                  and  Not exists ( SELECT 1 From tges_esp WHERE esp_idf_cod = lpr_idf_cod and esp_emp_cod = lpr_emp_cod and esp_pre_cod = lpr_pre_cod and esp_lpr_num = lpr_num) 
and pre_idf_cod = 'LIM' and pre_emp_cod = 'PRO' and pre_cod = 1076 
group by pre_idf_cod, pre_emp_cod,  pre_cli_cod, pre_mag_cod, lpr_art_cod, art_des, lpr_pre_cod  , lpr_diaminini, art_tipuni, lpr_unisto, nvl(lpr_cap_cod, 'zz*/'), nvl(lpr_pda_cod, 'zz*/') 
 
UNION ALL 
 
-- Lineas de presupuesto con artculos sin agrupar 
SELECT ALL  pre_idf_cod, pre_emp_cod, lpr_pre_cod pre_cod,  pre_cli_cod, pre_mag_cod, 
lpr_art_cod lpr_art_cod, lpr_num lpr_num, lpr_diaminini, 
lpr_uni dsp_uni, 
lpr_des dsp_des  , art_tipuni,  lpr_unisto, nvl(lpr_cap_cod, 'zz*/') lpr_cap_cod, nvl(lpr_pda_cod, 'zz*/') lpr_pda_cod 
from tges_lpr, tges_art, tges_pre 
where   lpr_idf_cod = art_idf_cod  and lpr_art_cod = art_cod 
    and lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and lpr_pre_cod = pre_cod 
    and nvl( art_grpfac, 'N') = 'N' 
   and ( (pre_est='A' and lpr_acc='S') OR pre_est <>'A') 
   /* No tomar los artculos que tienen escandallo */ 
   and  Not exists ( SELECT 1 From tges_esp WHERE esp_idf_cod = lpr_idf_cod and esp_emp_cod = lpr_emp_cod and esp_pre_cod = lpr_pre_cod and esp_lpr_num = lpr_num) 
and pre_idf_cod = 'LIM' and pre_emp_cod = 'PRO' and pre_cod = 1076 
 
UNION ALL 
 
-- Unidades servidas y no presupuestadas 
SELECT ALL pre_idf_cod, pre_emp_cod, pre_cod,  pre_cli_cod, pre_mag_cod, 
lac_art_cod lpr_art_cod , lac_num lpr_num , to_date(null) lpr_diaminini, 
       0 dsp_uni, art_des dsp_des, art_tipuni, to_number(null) lpr_unisto, 'zz*/' lpr_cap_cod, 'zz*/' lpr_pda_cod 
from tges_lac, tges_pre, tges_art 
where lac_idf_cod = pre_idf_cod and lac_emp_cod = pre_emp_cod and lac_pre_cod = pre_cod 
