CREATE OR REPLACE FORCE VIEW VGES_ALBTFAM
AS 
SELECT ALL idf, empresa, Codigo_Cliente, nombre_cliente, clase, FechaAlb, serie||'/'||Nalb Albaran,
 serie, nalb, far_cod codigo_familia, far_des nombre_familia,
 far_tip tipo,  ROUND(fges_canvi_divisa(idf, importe * pte * 0.01, div_cod, 'EURO', div_valeur, 1),2) importe,  codi_comercial
FROM tges_art, tges_far,
(
  SELECT ALL
  'ALBARAN' Tipo_alb,
  100 pte,
  alb_ser_cod serie,
  alb_num NAlb,
  alb_cls Clase,
  alb_cli_cod Codigo_Cliente,
  cli_nomfis nombre_cliente,
  alb_dia   FechaAlb,
  NVL(ela_art_cod, lac_art_cod) articulo,
  DECODE(ela_art_cod, NULL, NULL, lac_des) nombre_articulo,
  NVL(ela_unimet, 1) * lac_unimet unidades,
  NVL(ela_unimet * ela_pvp * lac_unimet * (1-nvl(lac_dto,0) *0.01) * (1- nvl(lac_dto002,0) *0.01), lac_imp) importe,
