 WHERE
   /* alb -< lac */
   lac_idf_cod = alb_idf_cod and
   lac_emp_cod = alb_emp_cod and
   lac_alb_numdoc = alb_numdoc and
   /* lac -< ela */
   ela_idf_cod (+) = lac_idf_cod and
   ela_emp_cod (+) = lac_emp_cod and
   ela_alb_numdoc (+) = lac_alb_numdoc and
   ela_lac_num (+) = lac_num and
   alb_idf_cod = cli_idf_cod and
   alb_cli_cod = cli_cod and
   /* Filtro */
