CREATE OR REPLACE FORCE VIEW VGES_AVI
AS 
SELECT avi_idf_cod identificador, avi_num numero, avi_emp_cod empresa, avi_sec_cod seccion,
       sec_nom NombreSeccion, avi_tav_cod CodTipAvi, tav_des TipoAviso,
	   decode((select count(*) from tges_avi a where a.avi_avi_num = a2.avi_num), 0, 'NO', 'SI') AVISOPADRE,
	   (select a1.avi_num from tges_avi a1 where rownum= 1 and a1.avi_tav_cod = 'PROJ'
	    START WITH a1.avi_num = a2.avi_num CONNECT BY PRIOR a1.avi_avi_num = avi_num) AVISOPREVPROJ,
	   avi_des Aviso, avi_cli_cod Cliente,
       cli_nomcom NombreCliente, cli_nif Nif,
       decode(avi_est, 1, 'Pendiente', 2, 'En curso', 3, 'Finalizado') Estado,
       avi_horprt HPrev,
       (select round(sum(nvl(lff_hor,0))+(sum(nvl(lff_min,0))/60),2) horfet
       from trhu_lff
       where lff_avi_num = avi_num and lff_idf_cod=avi_idf_cod) HReales,
       avi_horprt - (select round(sum(nvl(lff_hor,0))+(sum(nvl(lff_min,0))/60),2) horfet
       from trhu_lff where lff_avi_num = avi_num and lff_idf_cod=avi_idf_cod) Produc,
       (100-avi_pte) PorcRealiz, avi_ope_codreb Emisor, ope1.ope_nom NombreEmisor,
	   avi_ope_coddti Receptor, ope2.ope_nom NombreReceptor,
	   avi_ope_coddti002 Receptor2, ope3.ope_nom NombreReceptor2,
       avi_datent FechaAviso, avi_datprtini FecPrevini, avi_datprt FECPREVENTR,
       avi_datini FECINI, avi_datfin FECFIN ,avi_urg URGENCIA,
       avi_descur Descur, decode(avi_pre_cod, null, null, pre_ser_cod||'/'||pre_num||'/'||pre_ver) Presupuesto, avi_cap_cod Capitulo, avi_pda_cod Partida, avi_prj_num Proyecto,
       avi_per Solicitante, apl_cod Aplicacion, apl_nom NombreAplicacion, SYSDATE FECHA, avi_dataclpte ACTUALIZACION,
	   decode(nvl(avi_horprt,0), 0, 0, round(((select round(sum(nvl(lff_hor,0))+(sum(nvl(lff_min,0))/60),2) horfet
	                                           from trhu_lff
                                               where lff_avi_num = avi_num and lff_idf_cod=avi_idf_cod)
                                             *(100-avi_pte))/avi_horprt, 2)) PORREAL,
       avi_avi_num AVISOPREC,
	   (select a.avi_descur from tges_avi a where a.avi_num = a2.avi_avi_num) DESAVISOPREC,
   	   (select a.avi_tav_cod from tges_avi a where a.avi_num = a2.avi_avi_num) TIPAVISPREC,
	   avi_fbl FACTURABLE, avi_horfbl HORASFACTUR, avi_cnf CONFORMECLI, avi_obscli OBSCLI,
	   decode((select count(*) from tges_cai where cai_idf_cod=avi_idf_cod and cai_cli_cod=avi_cli_cod and cai_avi_num=avi_num), 0, 'NO', 'SI') INSTALADOCLIENTE,
	   avi_numper RECURSOS,
	   (select min(ffo_cln_dat) from trhu_ffo, trhu_lff where ffo_idf_cod = lff_idf_cod and ffo_cod = lff_ffo_cod and lff_avi_num = a2.avi_num and lff_idf_cod = a2.avi_idf_cod) DIAINIPARTE,
	   (select max(ffo_cln_dat) from trhu_ffo, trhu_lff where ffo_idf_cod = lff_idf_cod and ffo_cod = lff_ffo_cod and lff_avi_num = a2.avi_num and lff_idf_cod = a2.avi_idf_cod) DIAULTPARTE
FROM   tges_avi a2, tges_tav, tges_cli, tges_apl, trhu_sec, trhu_ope ope1, trhu_ope ope2, tges_pre, trhu_ope ope3
WHERE  tav_idf_cod = avi_idf_cod
       AND tav_emp_cod = avi_emp_cod
       AND tav_cod = avi_tav_cod
       AND cli_idf_cod(+) = avi_idf_cod
       AND cli_cod(+) = avi_cli_cod
       AND apl_idf_cod(+) = avi_idf_cod
       AND apl_ref(+) = avi_apl_ref
       AND avi_idf_cod = sec_idf_cod(+)
       AND avi_emp_cod = sec_emp_cod(+)
       AND avi_sec_cod = sec_cod(+)
       AND avi_idf_cod = ope1.ope_idf_cod(+)
       AND avi_ope_codreb = ope1.ope_cod(+)
       AND avi_idf_cod = ope2.ope_idf_cod(+)
       AND avi_ope_coddti = ope2.ope_cod(+)
       AND avi_idf_cod = ope3.ope_idf_cod(+)
       AND avi_ope_coddti002 = ope3.ope_cod(+)
	   and avi_idf_cod = pre_idf_cod(+)
	   and avi_emp_cod = pre_emp_cod(+)
	   and avi_pre_cod = pre_cod(+)
       order by avi_ope_coddti, avi_datent desc;


