CREATE OR REPLACE FORCE VIEW VGES_AVRPENALB
AS 
SELECT   tipo,
            avr_idf_cod,
            avr_emp_cod,
            tal_cod,
            tal_nom,
            avr_maq_cod,
            maq_nom,
            avr_num,
            avr_dattan,
            maq_emp_cod,
            ALBARAN,
            ALB_SER_COD,
            ALB_NUM,
            ALB_CLS,
            ALB_CLI_COD,
            ALB_DIA,
            alb_numdoc,
            LAC_ART_COD,
            LAC_UNI,
            LAC_DES,
            LAC_PRU,
            ALB_DIV_COD,
            ALB_VALDIVEUR,
            art_tipuni,
            FAR_SEQORD,
            empasg,
            prjasg,
            cliasg,
            nifasg,
            cliempmaq,
            avr_PRJ_NUM,
            prj_cli_cod,
            fges_clinif (avr_idf_cod, prj_cli_cod) prjclinif,
            NVL (empasg, maq_emp_cod) empfac,
            DECODE (NVL (empasg, maq_emp_cod),
                    avr_emp_cod, NVL (cliasg, prj_cli_cod),
                    fges_clifromemp (avr_idf_cod, NVL (empasg, maq_emp_cod)))
               clifac,
            NVL (
               prjasg,
               DECODE (NVL (empasg, maq_emp_cod),
                       avr_emp_cod, avr_prj_num,
                       NULL)
            )
               prjfac,
            ptefac
     FROM   (SELECT   1 tipo,
                      avr_idf_cod,
                      avr_emp_cod,
                      avr_maq_cod,
                      maq_nom,
                      avr_num,
                      avr_PRJ_NUM,
                      p.prj_cli_cod,
                      avr_dattan,
                      maq_emp_cod,
                      tal_cod,
                      tal_nom,
                         ALB_SER_COD
                      || '/'
                      || ALB_NUM
                      || DECODE (ALB_CLS, 1, '**')
                         ALBARAN,
                      ALB_SER_COD,
                      ALB_NUM,
                      ALB_CLS,
                      ALB_CLI_COD,
                      ALB_DIA,
                      ALB_NUMDOC,
                      LAC_ART_COD,
                      LAC_UNI,
                      NVL (LAC_DES, art_descur) || ' '
                      || DECODE (
                            NVL (ALP_NUMDOC, -1),
                            -1,
                            NULL,
                            '- Proveedor: ' || alp_pro_cod || ' - '
                            || (SELECT   pro_nomfis
                                  FROM   tges_pro
                                 WHERE   pro_idf_cod = alp_idf_cod
                                         AND pro_cod = alp_pro_cod)
                            || ' NAlb: '
                            || alp_num
                            || ' Fecha: '
                            || TO_CHAR (alp_diadoc, 'dd/mm/yyyy')
                         )
                         LAC_DES,
                        LAC_PRU
                      * (1 - (NVL (LAC_DTO, 0) / 100))
                      * (1 - (NVL (LAC_DTO002, 0) / 100))
                         lac_pru,
                      ALB_DIV_COD,
                      ALB_VALDIVEUR,
                      mao_emp_cod empasg,
                      mao_prj_num prjasg,
                      p2.prj_cli_cod cliasg,
                      cli_nif nifasg,
                      NVL (NVL (mao_pte004, mao_pte), 100) ptefac,
                      art_tipuni,
                      FAR_SEQORD,
                      fges_clifromemp (alb_idf_cod, maq_emp_cod) cliempmaq
               FROM   TGES_ALB,
                      TGES_LAC,
                      tges_avr,
                      trma_maq,
                      tges_tal,
                      tges_art,
                      tges_far,
                      tges_prj p,
                      trma_mao,
                      tges_prj p2,
                      tges_cli,
                      tges_alp
              WHERE       ALB_IDF_COD = LAC_IDF_COD
                      AND ALB_EMP_COD = LAC_EMP_COD
                      AND ALB_NUMDOC = LAC_ALB_NUMDOC
                      AND avr_idf_cod = alb_idf_cod
