                      AND tal_idf_cod = avr_idf_cod
                      AND tal_emp_cod = avr_emp_cod
                      AND tal_cod = avr_tal_cod
                      AND avr_est = 4
                      AND avr_alb_numdoc IS NULL
                      AND art_idf_cod = lac_idf_cod
                      AND art_cod = lac_art_cod
                      AND far_idf_cod = art_idf_cod
                      AND far_cod = art_far_cod
                      AND p.prj_idf_cod = avr_idf_cod
                      AND p.prj_emp_cod = avr_emp_cod
                      AND p.prj_num = avr_prj_num
