                                                  AND  NVL (mao_datfin,
                                                            avr_dattan)))
                      AND p2.prj_idf_cod(+) = mao_idf_cod
                      AND p2.prj_emp_cod(+) = mao_emp_cod
                      AND p2.prj_num(+) = mao_prj_num
                      AND cli_idf_cod(+) = p2.prj_idf_cod
                      AND cli_cod(+) = p2.prj_cli_cod
                      AND alp_idf_cod(+) = alb_idf_cod
                      AND alp_emp_cod(+) = alb_emp_cod
                      AND alp_numdoc(+) = ALB_ALP_NUMDOC
             UNION ALL
             SELECT   2 tipo,
                      avr_idf_cod,
                      avr_emp_cod,
                      avr_maq_cod,
                      maq_nom,
                      avr_num,
                      avr_prj_num,
                      p.prj_cli_cod,
