                                   AND cat_cod =
                                         FRHU_CAT_OPE (avr_idf_cod,
                                                       ffo_ope_cod,
                                                       alb_dia)),
                         lff_des
                      )
                         lac_des,
                      lff_pru lac_pru,
                      ALB_DIV_COD,
                      ALB_VALDIVEUR,
                      mao_emp_cod empasg,
                      mao_prj_num prjasg,
                      p2.prj_cli_cod cliasg,
                      cli_nif nifasg,
                      NVL (NVL (mao_pte004, mao_pte), 100) ptefac,
                      'PA' art_tipuni,
                      1000 FAR_SEQORD,
                      fges_clifromemp (avr_idf_cod, maq_emp_cod) cliempmaq
               FROM   tges_avr,
                      tges_alb,
                      trhu_lff,
                      trhu_ffo,
                      trma_maq,
                      trma_mao,
                      tges_cli,
                      tges_prj p2,
                      tges_prj p, trhu_cof
              WHERE       ffo_idf_cod = lff_idf_cod
                      AND ffo_cod = lff_ffo_cod
                      AND lff_idf_cod = alb_idf_cod
                      AND lff_emp_cod = alb_emp_cod
                      AND lff_lacnumdoc = alb_numdoc
