CREATE OR REPLACE FORCE VIEW VGES_BASES_PRE
AS 
SELECT 
lpr_idf_cod, 
lpr_emp_cod, 
lpr_pre_cod, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc001 ) lpr_opc001, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc002 ) lpr_opc002, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc003 ) lpr_opc003, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc004 ) lpr_opc004, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc005 ) lpr_opc005, 
pre_cli_cod, 
cli_recequ, 
lpr_iva_cod, 
DECODE(cli_recequ, 'S', iva_req , 0) iva_req, 
iva_pte, 
-- Bruto sin descuentos 
ROUND(SUM(lpr_bruto), div_decimp)  brut , 
-- Bruto aplicando descuentos 
ROUND(SUM(lpr_imp), div_decimp)  brut_dte, 
--  Base imponible sin descuentos 
ROUND(SUM(lpr_bruto), div_decimp)  basimp, 
-- Base imponible, aplicando descuentos 
ROUND(SUM(lpr_imp * (1 - nvl(pre_dte, 0) / 100)* 
(1 - nvl(pre_dte002, 0) / 100)), div_decimp) basimp_dte, 
-- Iva sin aplicar Dtos 
ROUND(SUM(lpr_bruto) * iva_pte /100, div_decimp)  iva, 
-- Iva aplicando Dtos 
ROUND(SUM(lpr_imp * (1 - nvl(pre_dte, 0) /100)* 
(1 - nvl(pre_dte002, 0) /100)) * iva_pte /100, div_decimp) iva_dte, 
-- Req sin dtos 
ROUND(SUM(lpr_bruto) * DECODE(cli_recequ , 'S' , iva_req , 0) /100, div_decimp) req, 
-- Req aplicando dtos 
ROUND(SUM(lpr_imp * (1 - nvl(pre_dte, 0) /100)* 
(1 - nvl(pre_dte002, 0) /100)) * DECODE(cli_recequ , 'S' , iva_req , 0) /100, div_decimp) req_dte, 
-- Total sin descuentos 
ROUND(SUM(lpr_bruto), div_decimp) + 
ROUND(SUM(lpr_bruto) * iva_pte /100, div_decimp) + 
ROUND(SUM(lpr_bruto) * DECODE(cli_recequ ,  'S' ,  iva_req ,  0) /100, div_decimp) total, 
--Total aplicando descuentos 
ROUND(SUM(lpr_imp * ( 1 - nvl(pre_dte, 0) / 100)* ( 1 - nvl(pre_dte002, 0) / 100)), div_decimp) + 
ROUND(SUM(lpr_imp * ( 1 - nvl(pre_dte, 0) / 100)* ( 1 - nvl(pre_dte002, 0) / 100)) * iva_pte /100, div_decimp) + 
ROUND(SUM(lpr_imp * ( 1 - nvl(pre_dte, 0) / 100)* ( 1 - nvl(pre_dte002, 0) / 100)) * 
DECODE(cli_recequ ,  'S' ,  iva_req ,  0)  /100, div_decimp)  total_dte 
FROM 
TGES_IVA, 
( 
SELECT 
lpr_idf_cod, 
lpr_emp_cod, 
lpr_pre_cod, 
nvl( lpr_opc001, 'N' ) lpr_opc001, 
nvl( lpr_opc002, 'N' ) lpr_opc002, 
nvl( lpr_opc003, 'N' ) lpr_opc003, 
nvl( lpr_opc004, 'N' ) lpr_opc004, 
nvl( lpr_opc005, 'N' ) lpr_opc005, 
nvl( lpr_opc001, 'N' ) || 
nvl( lpr_opc002, 'N' ) || 
nvl( lpr_opc003, 'N' ) || 
nvl( lpr_opc004, 'N' ) || 
nvl( lpr_opc005, 'N' ) lpr_opc, 
pre_cli_cod, 
pre_div_cod, 
pre_dte, 
pre_dte002, 
lpr_uni, 
lpr_pru, 
lpr_imp, 
cli_recequ, 
div_decimp, 
/*fges_lac_imp(lpr_idf_cod, 
lpr_uni, lpr_pru, 0, 0, 
lpr_lla, lpr_amp, lpr_alr, 
div_decimp, art_dim) lpr_bruto, */
fges_lac_imp(lpr_idf_cod, 
lpr_unimet, lpr_pru, 0, 0, 
null, null, null, 
div_decimp, 0, 'N') lpr_bruto, --> mateu i xisco, 25/9/09: no tenir en compte cap dimensi ni descompte
NVL(scl_iva_cod, NVL(cli_iva_cod, art_iva_cod)) lpr_iva_cod 
FROM TGES_LPR , TGES_ART, TGES_CLI, TGES_SCL, TGES_PRE, TGES_DIV 
WHERE 
/* Pre y LPR */ 
lpr_pre_cod = pre_cod AND lpr_emp_cod = pre_emp_cod AND lpr_idf_cod = pre_idf_cod AND 
/* ART y LPR */ 
art_cod = lpr_art_cod AND art_idf_cod = lpr_idf_cod AND 
/* CLI y PRE */ 
cli_cod = pre_cli_cod AND cli_idf_cod = pre_idf_cod AND 
/* SCL y PRE */ 
scl_cli_cod (+) = pre_cli_cod AND 
scl_idf_cod (+) = pre_idf_cod AND 
scl_cod (+)= pre_scl_cod      AND 
/* PRE y DIV */ 
pre_idf_cod = div_idf_cod AND 
pre_div_cod = div_cod  AND 
/* Condicin de Aceptado */ 
(pre_est NOT IN ('A','C') OR lpr_acc = 'S') 
) 
WHERE 
/* IVA y LPR */ 
iva_cod = lpr_iva_cod AND 
iva_idf_cod = lpr_idf_cod 
GROUP BY 
lpr_idf_cod, 
lpr_emp_cod, 
lpr_pre_cod, 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc001 ), 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc002 ), 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc003 ), 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc004 ), 
decode( lpr_opc, 'NNNNN', 'S', 
                 lpr_opc005 ), 
pre_cli_cod, 
lpr_iva_cod, 
iva_pte, 
iva_req, 
cli_recequ, 
div_decimp;


