 LAC_TCS_PTE,
 LAC_UNIMET,
 LAC_PDT_NUMSER,
 --
 FAR_COD,
 FAR_TIP,
 --
 lac_imp,
 DECODE(far_tip, 'M', lac_imp, 0) Imp_Material,
 --
 DECODE(far_tip, 'O', lac_unimet, 0) Uni_MObra,
 --
 DECODE(far_tip, 'O', lac_imp, 0) Imp_MObra,
