 DECODE(far_tip, 'M',
   Fges_LacCost(alb_idf_cod, alb_emp_cod, alb_numdoc, lac_num, alb_div_cod, lac_art_cod, lac_unimet),
     0) Cost_Material,
  --
 DECODE(far_tip, 'O',
   Fges_LacCost(alb_idf_cod, alb_emp_cod, alb_numdoc, lac_num,alb_div_cod, lac_art_cod, lac_unimet),
     0) Cost_MObra
FROM
 tges_alb, tges_lac, tges_art, tges_far
WHERE
  /* alb  -> lac */
  alb_idf_cod = lac_idf_cod and
  alb_emp_cod = lac_emp_cod and
  alb_numdoc = lac_alb_numdoc and
  /* lac -> art */
  lac_idf_cod = art_idf_cod and
  lac_art_cod = art_cod and
  /* art -> far */
  art_idf_cod = far_idf_cod and
  art_far_cod = far_cod and
  /* Filtro */
  art_cst = 'N'
