SELECT /* Artculos escandallo */
 alb_idf_cod,
 alb_emp_cod,
 alb_numdoc,
 alb_ser_cod,
 alb_num,
 ALB_REF,
 ALB_SER_CODFAC,
 ALB_FAC_CLS,
