CREATE OR REPLACE FORCE VIEW VGES_CMGUNIPEN
AS 
select cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod, lmg_num,lmg_uni, nvl(sum(lac_uni),0)  lmg_uniser
from tges_cmg, tges_lmg, tges_lac
where cmg_est = 1
and lmg_idf_cod = cmg_idf_cod and lmg_emp_cod = cmg_emp_cod and lmg_cmg_num = cmg_num
and lac_idf_cod(+) = lmg_idf_cod and lac_emp_cod(+) = lmg_emp_cod and lac_cmg_num(+) = lmg_cmg_num
and lac_lmg_num(+) = lmg_num and lac_art_cod(+) = lmg_art_cod
group by cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod,lmg_num,lmg_uni
having ( lmg_uni - nvl(sum(lac_uni),0) ) > 0
order by cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod;


