from ( 
  select IMP_IDF_COD, IMP_EMP_COD, IMP_SER_COD, IMP_ALBNUM, IMP_DIA, IMP_TPV_COD, IMP_IMP Imptpv, 0 ImpGes  from tges_imp_tpv 
  union all 
  select fac_idf_cod alb_idf_cod, fac_emp_cod alb_emp_cod, fac_ser_cod alb_ser_cod, fac_num alb_num, fac_dia alb_dia, 
