CREATE OR REPLACE FORCE VIEW VGES_CONPREFAR
AS 
SELECT
  LPR_IDF_COD  IDENTIFICADOR,
  LPR_EMP_COD  EMPRESA,
  LPR_PRE_COD  ID_INTERNO,
  ART_FAR_COD  CODIGO_FAMILIA,
  FAR_DES      NOMBRE_FAMILIA,
  PRE_EST      ESTADO_PRESUP,
  PRE_CLI_COD  CODIGO_CLIENTE,
  PRE_NOMCLI   NOMBRE_CLIENTE,
  PRE_DIA      FECHA_PRESUP,
  PRE_DIV_COD  DIVISA_PRESUP,
  PRE_OPE_COD  COMERCIAL,
  PRESUP       PRESUPUESTO,
  SUM(VALPRE)  VALOR_PRESUP
 FROM
  (SELECT /*+ RULE*/
    LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD,
	ART_FAR_COD,
	PRE_EST,
	PRE_CLI_COD, PRE_NOMCLI,
	PRE_DIA,
    PRE_DIV_COD,
    PRE_OPE_COD,
    PRE_SER_COD || '/' || PRE_NUM || '/' || PRE_VER PRESUP,
    SUM(LPR_IMP) AS VALPRE
   FROM
    TGES_LPR, TGES_ART,TGES_PRE
   WHERE
    LPR_IDF_COD = PRE_IDF_COD AND
    LPR_EMP_COD = PRE_EMP_COD AND
    LPR_PRE_COD = PRE_COD AND
    ART_IDF_COD = LPR_IDF_COD AND
    ART_COD = LPR_ART_COD AND
    PRE_EST IN ('A', 'C') AND
    LPR_ACC = 'S' AND
    NOT EXISTS
     (SELECT 1 FROM TGES_ESP
       WHERE
        ESP_IDF_COD = LPR_IDF_COD AND
        ESP_EMP_COD = LPR_EMP_COD AND
        ESP_PRE_COD = LPR_PRE_COD AND
        ESP_LPR_NUM = LPR_NUM)
  GROUP BY
    LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD,
	ART_FAR_COD,
	PRE_EST,
	PRE_CLI_COD, PRE_NOMCLI,
	PRE_DIA,
    PRE_DIV_COD,
    PRE_OPE_COD,
    PRE_SER_COD || '/' || PRE_NUM || '/' || PRE_VER
  UNION ALL
   SELECT /*+ RULE*/
    LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD,
	ART_FAR_COD,
	PRE_EST,
	PRE_CLI_COD, PRE_NOMCLI,
	PRE_DIA,
    PRE_DIV_COD,
    PRE_OPE_COD,
    PRE_SER_COD || '/' || PRE_NUM || '/' || PRE_VER PRESUP,
	SUM(ROUND(LPR_UNI * ESP_UNIMET * ESP_PVP, DIV_DECIMP)) AS VALPRE
   FROM
    TGES_ESP, TGES_LPR, TGES_ART, TGES_PRE, TGES_DIV
   WHERE
    LPR_IDF_COD = PRE_IDF_COD AND
	LPR_EMP_COD = PRE_EMP_COD AND
    LPR_PRE_COD = PRE_COD AND
    PRE_IDF_COD = DIV_IDF_COD AND
	PRE_DIV_COD = DIV_COD AND
    LPR_IDF_COD = ESP_IDF_COD AND
	LPR_EMP_COD = ESP_EMP_COD AND
    LPR_PRE_COD = ESP_PRE_COD AND
	LPR_NUM = ESP_LPR_NUM AND
    ART_IDF_COD = ESP_IDF_COD AND
	ART_COD = ESP_ART_COD AND
    PRE_EST IN ('A', 'C') AND
	LPR_ACC = 'S'
   GROUP BY
    LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD,
	ART_FAR_COD,
	PRE_EST,
	PRE_CLI_COD, PRE_NOMCLI,
	PRE_DIA,
    PRE_DIV_COD,
    PRE_OPE_COD,
    PRE_SER_COD || '/' || PRE_NUM || '/' || PRE_VER
  ), TGES_FAR
 WHERE
  LPR_IDF_COD = FAR_IDF_COD AND
  ART_FAR_COD = FAR_COD
 GROUP BY
  LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD,
  ART_FAR_COD,
  FAR_DES,
  PRE_EST,
  PRE_CLI_COD, PRE_NOMCLI,
  PRE_DIV_COD,
  PRE_OPE_COD,
  PRE_DIA,
  PRESUP;


