CREATE OR REPLACE FORCE VIEW VGES_COV
AS 
(
select alb_idf_cod as IDF, alb_emp_cod as EMPRESA,
alb_ope_codcml as COMERCIAL, fac_nomfiscli as NOMBRE, alb_fac_cls AS TP,alb_ser_codfac as SERFRA,
