lac_idf_cod = alb_idf_cod and lac_emp_cod = alb_emp_cod and
lac_alb_numdoc = alb_numdoc and
fac_idf_cod = alb_idf_cod and fac_emp_cod = alb_emp_cod and fac_cls = alb_fac_cls and
fac_ser_cod = alb_ser_codfac and fac_num = alb_fac_num and
art_idf_cod = lac_idf_cod and art_cod = lac_art_cod and
far_cod = art_far_cod and
cal_idf_cod = lac_idf_cod and
cal_emp_cod = lac_emp_cod and cal_alb_numdoc = lac_alb_numdoc and
cal_lac_num = lac_num and
ope_idf_cod = alb_idf_cod and ope_cod = alb_ope_codcml  --and ope_gre_cod = '0001'
group by   alb_idf_cod,alb_emp_cod, alb_ope_codcml, fac_nomfiscli, alb_ser_codfac, alb_fac_cls,alb_fac_num,fac_dia,
alb_ser_cod,alb_num, lac_num, alb_dia, lac_art_cod,lac_des,lac_uni,
round(lac_pru / alb_valdiveur,2) ,lac_dto,
lac_dto002,fac_dto,fac_dto002,far_cmiven,
round(lac_imp/alb_valdiveur,2) ,
round(lac_uni * lac_pru * ( 1 - nvl(lac_dto,0) * 0.01 ) * ( 1 - nvl(lac_dto002,0) * 0.01 )
* ( 1 - nvl(fac_dto,0) * 0.01 )* ( 1 - nvl(fac_dto002,0) * 0.01 ) /alb_valdiveur,2));


