CREATE OR REPLACE FORCE VIEW VGES_FACTFAM
AS 
SELECT ALL idf, empresa, Codigo_Cliente, nombre_cliente, clase, FechaFra, serie||'/'||Nfra Factura,
 serie, nfra, far_cod codigo_familia, far_des nombre_familia,
 far_tip tipo,  ROUND(fges_canvi_divisa(idf, importe * pte * 0.01, div_cod, 'EURO', div_valeur, 1),2) importe,  codi_comercial
FROM tges_art, tges_far,
(
  SELECT ALL
  'ALBARAN' Tipo_fra,
  100.00 pte,
  fac_ser_cod serie,
  fac_num Nfra,
  fac_cls Clase,
  fac_cli_cod Codigo_Cliente,
  fac_nomfiscli nombre_cliente,
  fac_dia   FechaFra,
  NVL(ela_art_cod, lac_art_cod) articulo,
  DECODE(ela_art_cod, NULL, NULL, lac_des) nombre_articulo,
  NVL(ela_unimet, 1) * lac_unimet unidades,
  NVL(ela_unimet * ela_pvp * lac_unimet * (1-nvl(lac_dto,0) *0.01) * (1- nvl(lac_dto002,0) *0.01), lac_imp) importe,
  fac_dto,
  fac_dto002,
  alb_div_cod div_cod,
  alb_valdiveur div_valeur,
  cli_ope_cod codi_comercial,
  fac_idf_cod idf,
  fac_emp_cod empresa
 FROM tges_fac, tges_alb, tges_lac, tges_ela, tges_cli, tges_art
 WHERE
   /* fac -< alb */
   alb_idf_cod = fac_idf_cod and
   alb_emp_cod = fac_emp_cod and
   alb_ser_codfac = fac_ser_cod and
   alb_fac_num = fac_num and
   alb_fac_cls = fac_cls and
   /* alb -< lac */
   lac_idf_cod = alb_idf_cod and
   lac_emp_cod = alb_emp_cod and
   lac_alb_numdoc = alb_numdoc and art_idf_cod=lac_idf_cod and art_cod=lac_art_cod and
   /* lac -< ela */
   ela_idf_cod (+) = lac_idf_cod and
   ela_emp_cod (+) = lac_emp_cod and
   ela_alb_numdoc (+) = lac_alb_numdoc and
   ela_lac_num (+) = lac_num and
   fac_idf_cod = cli_idf_cod and
   fac_cli_cod = cli_cod and
   /* Filtro */
   fac_cer_num is null AND (ART_CST='S' and fges_lac_ela_0(lac_idf_cod, lac_emp_cod, lac_alb_numdoc, lac_num, ART_CST)='N')
--
union all
  SELECT ALL
  'ALBARAN' Tipo_fra,
  100.00 pte,
  fac_ser_cod serie,
  fac_num Nfra,
  fac_cls Clase,
  fac_cli_cod Codigo_Cliente,
  fac_nomfiscli nombre_cliente,
  fac_dia   FechaFra,
  lac_art_cod articulo,
  null/*lac_des*/ nombre_articulo,
  lac_unimet unidades,
  lac_imp importe,
  fac_dto,
  fac_dto002,
  alb_div_cod div_cod,
  alb_valdiveur div_valeur,
  cli_ope_cod codi_comercial,
  fac_idf_cod idf,
  fac_emp_cod empresa
 FROM tges_fac, tges_alb, tges_lac, tges_cli, tges_art
 WHERE
   /* fac -< alb */
   alb_idf_cod = fac_idf_cod and
   alb_emp_cod = fac_emp_cod and
   alb_ser_codfac = fac_ser_cod and
   alb_fac_num = fac_num and
   alb_fac_cls = fac_cls and
   /* alb -< lac */
   lac_idf_cod = alb_idf_cod and
   lac_emp_cod = alb_emp_cod and
   lac_alb_numdoc = alb_numdoc and art_idf_cod=lac_idf_cod and art_cod=lac_art_cod and
   fac_idf_cod = cli_idf_cod and
