 'ALBARAN' Tipo_fra,
 100 pte,
 fac_ser_cod serie,
 fac_num Nfra,
 fac_cls Clase,
 fac_cli_cod Codigo_Cliente,
 fac_nomfiscli nombre_cliente,
 fac_dia   FechaFra,
 NVL(ela_art_cod, lac_art_cod) articulo,
 DECODE(ela_art_cod, NULL, NULL, lac_des) nombre_articulo,
 NVL(ela_unimet, 1) * lac_unimet unidades,
 NVL(ela_unimet * ela_pvp * lac_unimet * (1-nvl(lac_dto,0) *0.01) * (1- nvl(lac_dto002,0) *0.01), lac_imp) importe,
 fac_dto,
 fac_dto002,
 alb_div_cod div_cod,
 alb_valdiveur div_valeur,
 cli_ope_cod codi_comercial,
 fac_idf_cod idf,
 fac_emp_cod empresa
FROM
 tges_fac,
 tges_alb,
 tges_lac,
 tges_ela,
 tges_cli
WHERE
 /* fac -< alb */
 alb_idf_cod = fac_idf_cod and
 alb_emp_cod = fac_emp_cod and
 alb_ser_codfac = fac_ser_cod and
 alb_fac_num = fac_num and
 alb_fac_cls = fac_cls and
 /* alb -< lac */
 lac_idf_cod = alb_idf_cod and
 lac_emp_cod = alb_emp_cod and
 lac_alb_numdoc = alb_numdoc and
 /* lac -< ela */
 ela_idf_cod (+) = lac_idf_cod and
 ela_emp_cod (+) = lac_emp_cod and
 ela_alb_numdoc (+) = lac_alb_numdoc and
 ela_lac_num (+) = lac_num and
 fac_idf_cod = cli_idf_cod and
 fac_cli_cod = cli_cod and
 /* Filtro */
 fac_cer_num is null
UNION ALL
SELECT ALL
 'CERTIFICACION' Tipo_fra,
 fac_ptecer pte,
 fac_ser_cod serie,
 fac_num Nfra,
 fac_cls Clase,
 fac_cli_cod Codigo_Cliente,
 fac_nomfiscli nombre_cliente,
 fac_dia   FechaFra,
 NVL(esp_art_cod, lpr_art_cod) articulo,
 DECODE(esp_art_cod, NULL, NULL, lpr_des) nombre_articulo,
 NVL(esp_unimet, 1) * (LCE_UNIACT - LCE_UNIANT) unidades,
 NVL(esp_unimet * esp_pvp * (LCE_UNIACT - LCE_UNIANT) * (1-nvl(lce_dte,0) *0.01) * (1- nvl(lce_dte002,0) *0.01), LCE_VALACT - LCE_VALANT) importe,
 fac_dto,
 fac_dto002,
 cer_div_cod   div_cod,
 cer_valdiveur div_valeur,
 cli_ope_cod codi_comercial,
 fac_idf_cod idf,
 fac_emp_cod empresa
FROM
 tges_fac,
 tges_cer,
 tges_lce,
 tges_lpr, /* para tomar cdigo y descripcin */
 tges_esp,
 tges_cli
WHERE
 /* fac >- cer */
 cer_idf_cod = fac_idf_cod and
 cer_emp_cod = fac_emp_cod and
 cer_pre_cod = fac_pre_cod and
 cer_num = fac_cer_num and
 /* cer -< lce */
 cer_idf_cod = lce_idf_cod and
 cer_emp_cod = lce_emp_cod and
 cer_pre_cod = lce_pre_cod and
 cer_num = lce_cer_num and
 /* lce - lpr */
 lce_idf_cod = lpr_idf_cod and
 lce_emp_cod = lpr_emp_cod and
 lce_pre_cod = lpr_pre_cod and
 lce_lpr_num = lpr_num and
 /* lce -< esp */
 lce_idf_cod = esp_idf_cod (+) and
 lce_emp_cod = esp_emp_cod (+) and
 lce_pre_cod = esp_pre_cod (+) and
 lce_lpr_num = esp_lpr_num (+) and
 fac_idf_cod = cli_idf_cod and
 fac_cli_cod = cli_cod
 /* Filtro */
)
WHERE
 art_cod  = articulo and
 far_idf_cod = art_idf_cod and
 far_cod = art_far_cod and
 importe<>0;


CREATE OR REPLACE FORCE VIEW VGES_FAC_ASICMP
AS 
SELECT   tip,
            fac_idf_cod,
            fac_emp_cod,
            fac_ser_cod,
            fac_cls,
            fac_num,
            /*fac_empcmp,*/
            fac_exccmp,
            fac_dricmp,
            fac_asicmp
     FROM   (SELECT   'FAC' AS tip,
                      fac_idf_cod,
                      fac_emp_cod,
                      fac_ser_cod,
                      fac_cls,
                      fac_num,
                     /* ltrim(rtrim(substr(emp_codcmp,1,4))) as fac_empcmp,*/
                      fac_exccmp,
                      fac_dricmp,
                      fac_asicmp
               FROM   tges_fac, tges_emp
              WHERE   NVL (fac_asicmp, -1) > 0
                and emp_idf_cod=fac_idf_cod and emp_cod=fac_emp_cod
             UNION ALL
             -- movs de caixa
             SELECT   'MDC',
                      vcx_idf_cod,
                      vcx_emp_cod,
                      vcx_ser_cod,
                      vcx_fac_cls,
                      vcx_fac_num,
                      /*ltrim(rtrim(substr(emp_codcmp,1,4))) as fac_empcmp,*/
                      mdc_exccmp,
                      mdc_dricmp,
                      mdc_asicmp
               FROM   tges_mdc, tges_vcx, tges_emp
              WHERE       NVL (mdc_asicmp, -1) > 0
                      AND VCX_IDF_COD = mdc_idf_cod
                      AND VCX_EMP_COD = mdc_emp_cod
                      AND VCX_CXA_COD = mdc_cxa_cod
                      AND VCX_MDC_NUM = mdc_num
                and emp_idf_cod=mdc_idf_cod and emp_cod=mdc_emp_cod
             UNION ALL
             SELECT   'MDC002',
                      vcx_idf_cod,
                      vcx_emp_cod,
                      vcx_ser_cod,
                      vcx_fac_cls,
                      vcx_fac_num,
                      /*ltrim(rtrim(substr(emp_codcmp,1,4))) as fac_empcmp,*/
                      mdc_exccmp002,
                      mdc_dricmp,
                      mdc_asicmp002
               FROM   tges_mdc, tges_vcx, tges_emp
              WHERE       NVL (mdc_asicmp002, -1) > 0
                      AND VCX_IDF_COD = mdc_idf_cod
                      AND VCX_EMP_COD = mdc_emp_cod
                      AND VCX_CXA_COD = mdc_cxa_cod
                      AND VCX_MDC_NUM = mdc_num
                  and emp_idf_cod=mdc_idf_cod and emp_cod=mdc_emp_cod);


CREATE OR REPLACE FORCE VIEW VGES_FAC_PROJ_COMPRES
AS 
SELECT distinct emp_nomfis Empresa, fpv_doc Factura, fpv_dia Data, pro_cod CodiProv, pro_nomcom NomProv, prj_num CodiProj, prj_nom NomProj, decode(fpv_cls, '0', 'A', '1', 'B') Tipus 
  FROM tges_fpv, tges_pro, tges_alp, tges_prj, tges_emp 
 WHERE pro_idf_cod = fpv_idf_cod 
   AND pro_cod = fpv_pro_cod 
   AND fpv_idf_cod = alp_idf_cod 
   AND fpv_emp_cod = alp_emp_cod 
   AND fpv_num = alp_fpv_num 
   AND prj_num(+) = alp_prj_num 
   AND prj_emp_cod(+) = alp_emp_cod 
   AND prj_idf_cod(+) = alp_idf_cod 
   and fpv_idf_cod = emp_idf_cod 
   and fpv_emp_cod = emp_cod 
   AND fpv_idf_cod = '8140';


CREATE OR REPLACE FORCE VIEW VGES_FAC_PROJ_VENDES
AS 
SELECT emp_nomfis Empresa, fac_ser_cod||'/'||fac_num Factura, fac_dia Data, cli_cod CodiCli, cli_nomcom NomCli, prj_num CodiProj, prj_nom NomProj, decode(fac_cls, '0', 'A', '1', 'B') Tipus 
