CREATE OR REPLACE FORCE VIEW VGES_FPVRSPOBR
AS 
SELECT /*+ ORDERED */ 
              fpv_idf_cod, fpv_emp_cod, fpv_num, fpv_doc, fpv_pro_cod, REPLACE (fpv_nomfispro, '''', '') fpv_nomfispro, 
              fpv_dia, fpv_diareb, fpv_datcre, fpv_bim, fpv_quaiva, fpv_tot, 
              decode(fges_countprjfpvalp (fpv_idf_cod, fpv_emp_cod, fpv_num), 1, alp_prj_num, 'Varios') alp_prj_num, 
              decode(fges_countprjfpvalp (fpv_idf_cod, fpv_emp_cod, fpv_num), 1, prj_nom, 'Mltiples projectos') prj_nom, prj_ope_cod CodJefeObra, 
              o1.ope_nom NomJefeObra, 
              prj_ope_codcgr CodJefeGrupo, o2.ope_nom NomJefeGrupo, doc_pth, doc_nomfit, doc_dap_cod, fpv_cnf, FPV_VALPRV, FPV_CNFGRP, 
              SUM (DECODE (NVL (ALP_CNF, 'N'), 'N', 1, 0)) AlbPenCnf, 
              SUM (DECODE (NVL (ALP_CNFJDG, 'N'), 'N', 1, 0)) AlbPenCnfjdg, 
              (fges_countprjfpvalp (fpv_idf_cod, fpv_emp_cod, fpv_num)) CountProjectes, 
              fpv_rtn, doc_seq, fpv_ope_codjfo, fpv_ope_codgrp, fpv_scp_cod 
       FROM   tges_fpv, tges_alp, tges_prj, trhu_ope o1, trhu_ope o2, tlim_doc 
      WHERE   alp_idf_cod = fpv_idf_cod and alp_emp_cod = fpv_emp_cod and alp_fpv_num = fpv_num 
              and prj_idf_cod = alp_idf_cod and prj_emp_cod = alp_emp_cod and prj_num = alp_prj_num 
              and o1.ope_idf_cod = prj_idf_cod and o1.ope_cod = prj_ope_cod and o2.ope_idf_cod = prj_idf_cod 
              and o2.ope_cod = PRJ_OPE_CODCGR and doc_clc_cod(+) = fpv_idf_cod and doc_ori_cod(+) = 'FPV' 
              and doc_dap_cod(+) = 
                    DECODE ( 
                       flim_exidoc ( 
                          fpv_idf_cod, 
                             fpv_emp_cod 
                          || '_' 
                          || fpv_pro_cod 
                          || '_' 
                          || TO_CHAR (fpv_dia, 'yyyy-mm-dd') 
                          || '_' 
                          || REPLACE (fpv_doc, '/', '-'), 
                          'FPV' 
                       ), 
                       'N', 
                          fpv_emp_cod 
                       || '_' 
                       || fpv_pro_cod 
                       || '_' 
                       || REPLACE (fpv_doc, '/', '-'), 
                       'S', 
                          fpv_emp_cod 
                       || '_' 
                       || fpv_pro_cod 
                       || '_' 
                       || TO_CHAR (fpv_dia, 'yyyy-mm-dd') 
                       || '_' 
                       || REPLACE (fpv_doc, '/', '-') 
                    ) 
   GROUP BY   fpv_idf_cod, fpv_emp_cod, fpv_num, fpv_doc, fpv_pro_cod, fpv_nomfispro, 
              fpv_dia, fpv_diareb, fpv_datcre, fpv_bim, fpv_quaiva, fpv_tot, alp_prj_num, 
              fpv_ope_codjfo, fpv_ope_codgrp, prj_nom, prj_ope_cod, o1.ope_nom, prj_ope_codcgr, 
              o2.ope_nom, fpv_cnf, FPV_VALPRV, FPV_CNFGRP, doc_pth, doc_nomfit, doc_dap_cod, 
              fpv_rtn, doc_seq, fpv_scp_cod;


