CREATE OR REPLACE FORCE VIEW VGES_IMPUTACLPR
AS 
select idf_cod, emp_cod, pre_cod, cap_cod, cap_des,cap_seq, pda_cod, pda_des, pda_seq,
	   art_cod, lpr_des, lpr_seq, pru, uni , unimet, imp, div_cod,div_valeur, Presup, sum(lacimp) as lacimp
from
(
 select lpr_idf_cod idf_cod, lpr_emp_cod emp_cod,
          lpr_pre_cod pre_cod, nvl(lpr_cap_cod, 'ZZZZ') cap_cod, nvl(cap_des, 'Sin Capitulo') cap_des, nvl(cap_seq,0) cap_seq,
		  nvl(lpr_pda_cod, 'ZZZZ') pda_cod, nvl(pda_des, 'Sin Partida') pda_des,nvl(pda_seq,0) pda_seq,
		  lpr_art_cod art_cod, lpr_des, nvl(lpr_seq,10) lpr_seq, lpr_pru pru,
          lpr_uni uni , lpr_unimet unimet,  lpr_imp imp,
          pre_div_cod div_cod, pre_valdiveur div_valeur,
          pre_ser_cod || '\' || pre_num || '\' || pre_ver Presup,
         fges_canvi_divisa(lac_idf_cod,lac_imp,alb_div_cod,pre_div_cod,alb_valdiveur,pre_valdiveur) as lacimp
     from tges_lpr, tges_cap, tges_pda, tges_pre, tges_lac, tges_alb
    where lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and
          lpr_pre_cod = pre_cod     and lpr_acc = 'S'             and
          lpr_idf_cod = cap_idf_cod(+) and lpr_emp_cod = cap_emp_cod(+) and
          lpr_pre_cod = cap_pre_cod(+) and lpr_cap_cod = cap_cod(+)     and
          lpr_idf_cod = pda_idf_cod(+) and lpr_emp_cod = pda_emp_cod(+) and
          lpr_pre_cod = pda_pre_cod(+) and lpr_cap_cod = pda_cap_cod(+) and
		  lpr_pda_cod = pda_cod(+)     and lac_idf_cod(+) = lpr_idf_cod    and
 		  lac_emp_cod(+) = lpr_emp_cod and lac_pre_cod(+) = lpr_pre_cod and
 		  lac_lpr_num(+) = lpr_num     and lac_idf_cod = alb_idf_cod(+) and
          lac_emp_cod = alb_emp_cod(+) and lac_alb_numdoc = alb_numdoc(+)
union all
 select lac_idf_cod idf_cod, lac_emp_cod emp_cod,
          lac_pre_cod pre_cod, nvl(lac_cap_cod, 'ZZZZ') cap_cod, nvl(cap_des, 'Sin Capitulo') cap_des,nvl(cap_seq,0) cap_seq,
		  nvl(lac_pda_cod, 'ZZZZ') pda_cod, nvl(pda_des, 'Sin Partida') pda_des,nvl(pda_seq,0) pda_seq,
		  'ZZZZ' as cod_art, 'Sin Linea' as lpr_des, 0 as lpr_seq,
		  0 pru,
          0 uni , 0 unimet,  0 imp,
          pre_div_cod div_cod, pre_valdiveur div_valeur,
          pre_ser_cod || '\' || pre_num || '\' || pre_ver Presup,
		  sum(fges_canvi_divisa(lac_idf_cod,lac_imp,alb_div_cod,pre_div_cod,alb_valdiveur,pre_valdiveur)) as lacimp
     from tges_lac, tges_cap, tges_pda, tges_pre, tges_alb
    where lac_idf_cod = alb_idf_cod and lac_emp_cod = alb_emp_cod and
          lac_alb_numdoc = alb_numdoc and lac_idf_cod = pre_idf_cod and
          lac_emp_cod = pre_emp_cod and lac_pre_cod = pre_cod     and
          lac_idf_cod = cap_idf_cod(+) and lac_emp_cod = cap_emp_cod(+) and
          lac_pre_cod = cap_pre_cod(+) and lac_cap_cod = cap_cod(+)     and
          lac_idf_cod = pda_idf_cod(+) and lac_emp_cod = pda_emp_cod(+) and
          lac_pre_cod = pda_pre_cod(+) and lac_cap_cod = pda_cap_cod(+) and
		  lac_pda_cod = pda_cod(+)     and lac_lpr_num is null
group by lac_idf_cod , lac_emp_cod, lac_pre_cod, nvl(cap_seq,0),nvl(lac_cap_cod, 'ZZZZ'), nvl(cap_des, 'Sin Capitulo'),
		  nvl(pda_seq,0),nvl(lac_pda_cod, 'ZZZZ'), nvl(pda_des, 'Sin Partida'),
          pre_div_cod , pre_valdiveur,
          pre_ser_cod || '\' || pre_num || '\' || pre_ver
)
group by  idf_cod , emp_cod , pre_cod, cap_seq,cap_cod, cap_des,pda_seq, pda_cod, pda_des,
		  lpr_seq,art_cod, lpr_des, pru, uni , unimet, imp, div_cod,div_valeur, Presup
order by  idf_cod, emp_cod, pre_cod, cap_seq,cap_cod, cap_des, pda_seq,pda_cod, pda_des,
		  lpr_seq,art_cod, lpr_des;


