CREATE OR REPLACE FORCE VIEW VGES_LCE
AS 
SELECT
 LCE_IDF_COD, LCE_EMP_COD, LCE_PRE_COD, LCE_LPR_NUM, LCE_CER_NUM,
 LCE_UNIANT,  LCE_VALANT,  LCE_UNIACT,  LCE_PRU,     LCE_VALACT,
 LCE_DTE,     LCE_DTE002,  LCE_OBS,
 --
 LPR_IDF_COD, LPR_EMP_COD, LPR_PRE_COD, LPR_NUM,
 LPR_ART_COD, LPR_SEQ,     LPR_DES,    LPR_CAP_COD,  LPR_PDA_COD,
 LPR_LLA,     LPR_AMP,     LPR_ALR,    LPR_REF,      LPR_UNIMET, LPR_UNI
FROM
 TGES_LCE, tges_lpr
WHERE
 lpr_idf_cod = lce_idf_cod and  --LPR-<LCE
 lpr_emp_cod = lce_emp_cod and
 lpr_pre_cod = lce_pre_cod and
 lpr_num = lce_lpr_num
UNION
SELECT
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, TO_NUMBER(NULL) LCE_LPR_NUM, CER_NUM,
 0 LCE_UNIANT, LCE_VALANT,  1 LCE_UNIACT,  LCE_VALACT, LCE_VALACT,
 TO_NUMBER(NULL) LCE_DTE,    TO_NUMBER(NULL) LCE_DTE002, TO_CHAR(NULL) LCE_OBS,
 --
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, TO_NUMBER(NULL) LCE_LPR_NUM,
 Fges_Parametre(cer_idf_cod, 'ART_ANC') LPR_ART_COD, TO_NUMBER(NULL) LPR_SEQ, APC_DES LPR_DES, TO_CHAR(NULL) LPR_CAP_COD, TO_CHAR(NULL) LPR_PDA_COD,
 TO_NUMBER(NULL) LPR_LLA, TO_NUMBER(NULL) LPR_AMP, TO_NUMBER(NULL) LPR_ALR, TO_CHAR(NULL) LPR_REF, TO_NUMBER(NULL) LPR_UNIMET, TO_NUMBER(NULL) LPR_UNI
FROM
 (
SELECT
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, CER_NUM, APC_DES,
 -SUM(DECODE(CER_NUM, APP_CER_NUM, 0, app_impseniva)) LCE_VALANT,
 -SUM(APP_IMPSENIVA) LCE_VALACT
FROM
 vges_appiva, tges_apc, tges_cer
WHERE
 cer_idf_cod = app_idf_cod AND
 cer_emp_cod = app_emp_cod AND
 cer_pre_cod = app_pre_codcer AND
 (
  (cer_ori = 'S' AND app_cer_num <= cer_num) OR (cer_ori = 'N' AND app_cer_num = cer_num)
 ) AND
 ---
 apc_idf_cod = app_idf_cod AND
 apc_emp_cod = APP_EMP_COD AND
 apc_pre_cod = APP_PRE_COD AND
 apc_num = APP_APC_NUM
GROUP BY
 CER_IDF_COD,  CER_EMP_COD, CER_PRE_COD, CER_NUM, APC_DES
 );


CREATE OR REPLACE FORCE VIEW VGES_LCECER
AS 
select tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, 
  lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
  LPR_ART_COD, esp_art_cod, lpr_des, LPR_LLA, LPR_AMP, LPR_ALR, LPR_SEQ, lpr_acc, lpr_num, 
  art_dim, art_fcs, art_pvp, art_marpvpcos, lpr_far_tip, lpr_uni, lpr_unimet, CosteUni, UniOri, ValOri, 
  CosteOri, UniNoOri, ValNoOri, CosteNoOri, UniPDCOri, ValPDCOri, CostePDCOri, UniPDCNoOri, ValPDCNoOri, 
  CostePDCNOOri, lpr_pru, LPR_DTO, LPR_DTO002, ImporteOri, ImporteNoOri, ImportePDCOri, ImportePDCNoOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteOri, 0), 0, null, round(((ImporteOri / CosteOri) -1) * 100,2)), 
         '2', case 
                when ImporteOri <> 0 then round(100 * (1 - (CosteOri / ImporteOri)),2) 
                when CosteOri <>0  then 0 
                when CosteOri = 0 and ImporteOri = 0 then null 
              end 
         ) MargenOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteNoOri, 0), 0, null, round(((ImporteNoOri / CosteNoOri) -1) * 100,2)), 
         '2', case 
                when ImporteNoOri <> 0 then round(100 * (1 - (CosteNoOri / ImporteNoOri)),2) 
                when CosteNoOri <>0  then 0 
                when CosteNoOri = 0 and ImporteNoOri = 0 then null 
              end 
         ) MargenNoOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
