              end 
         ) MargenPDCOri, 
  decode(fges_parametre(lpr_idf_cod, 'CALPVP', '1'), 
         '1', decode(nvl(CosteOri, 0), 0, null, round(((ImportePDCNoOri / CostePDCNoOri) -1) * 100,2)), 
         '2', case 
                when ImportePDCNoOri <> 0 then round(100 * (1 - (CostePDCNoOri / ImportePDCNoOri)),2) 
                when CostePDCNoOri <>0  then 0 
                when CostePDCNoOri = 0 and ImportePDCNoOri = 0 then null 
              end 
         ) MargenPDCNoOrI 
from 
( 
  select tipo, lpr_idf_cod, lpr_emp_cod, lce_cer_num, lpr_pre_cod, pre_Ser_cod, pre_num, pre_Ver, lpr_CAP_COD, CAP_DES, cap_seq, lpr_PDA_COD, PDA_DES, pda_seq, 
  LPR_ART_COD, esp_art_cod, lpr_des, LPR_LLA, LPR_AMP, LPR_ALR, LPR_SEQ, lpr_acc, lpr_num, 
  art_dim, art_fcs, art_pvp, art_marpvpcos, lpr_far_tip, lpr_uni, lpr_unimet, CosteUni, UniOri, ValOri, 
  decode 
  ( 
    fges_parametre(lpr_idf_cod, 'STO_DIM', 'N'), 
    'N', round(UniOri*CosteUni, 2), 
    'S', round(UniOri*CosteUni/* *nvl(lpr_lla * fges_parametre(lpr_idf_cod, 'lla_fc', 1), 1) * 
                               nvl(lpr_amp * fges_parametre(lpr_idf_cod, 'amp_fc', 0.01), 1) * 
