CREATE OR REPLACE FORCE VIEW VGES_LESCOS
AS 
select idf_cod, emp_cod, prj_num, etp_cod, etp_num, uce_cod, uce_des, les_cod, les_desred, les_tun_cod,
les_uniact, rle_cod,rle_des,rle_tun_cod, sum(rle_uniact) as rle_uniact, sum(cos_teo) as cos_teo,
sum (cos_rea) as cos_rea,
sum (cos_reacos) as cos_reacos
from
(
(
select 'T' as tip,les_idf_cod as idf_cod, les_emp_cod as emp_cod, les_prj_num as prj_num , les_etp_cod as etp_cod,
les_etp_num as etp_num , uce_cod as uce_cod , uce_des as uce_des , les_cod as les_cod, les_desred as les_desred, les_tun_cod as les_tun_cod,
les_uniact , rle_cod,rle_des, rle_tun_cod, nvl(rle_uni * les_uniact,0) as rle_uniact, nvl( rle_uni * rle_cosuni * les_uniact,0) as cos_teo,
0 as cos_rea, 0 as cos_reacos
from vges_les,  tges_uce, tges_rle
where les_idf_cod = uce_idf_Cod and les_emp_cod = uce_emp_cod and les_prj_num = uce_prj_num and les_etp_cod = uce_etp_cod
and les_etp_num = uce_etp_num and les_uce_seq = uce_seq and les_etp_num > 0
and rle_idf_cod = les_idf_cod and rle_emp_cod = les_emp_cod and rle_prj_num = les_prj_num and rle_etp_cod = les_etp_cod and rle_etp_num = les_etp_num
and rle_les_seq = les_seq and rle_etp_num > 0
)
union all
(
select 'R' as tip, a.etp_idf_cod as idf_cod , a.etp_emp_cod as emp_cod, a.etp_prj_num as prj_num, a.etp_cod as etp_cod,
