0 as les_uniact, nvl(rle_cod,'ZZZZ'),nvl(rle_des,'ZZZZ-Sin Recurso') , nvl(rle_tun_cod,''), 0 as rle_uniact, 0 as cos_teo, sum(imp) as cos_rea,
sum(cos) as cos_reacos
from vges_lescosrea a, tges_etp b
where a.etp_idf_cod = b.etp_idf_cod and a.etp_emp_cod = b.etp_emp_cod and a.etp_prj_num = b.etp_prj_num
and a.etp_cod = b.etp_cod and a.etp_num = b.etp_num and dia <= nvl(b.etp_diafin,to_date('31/12/5000','dd/mm/yyyy'))
group by a.etp_idf_cod,a.etp_emp_cod, a.etp_prj_num, a.etp_cod, a.etp_num, uce_cod,uce_des, les_cod, les_desred, les_tun_cod,rle_cod,rle_des, rle_tun_cod
)
union all
(
select 'N' as tip,a.etp_idf_cod as idf_cod , a.etp_emp_cod as emp_cod , a.etp_prj_num as prj_num, a.etp_cod as etp_cod,
a.etp_num as etp_num,  'ZZ' as uce_cod, 'ZZ-Sin Asignar' as uce_des, 'ZZZ' as les_cod, 'ZZZ-Sin Partida' as les_desred, '' as les_tun_cod,
0 as les_uniact, 'ZZZZ' as rle_cod, 'ZZZZ-Sin Recurso' as rle_des, '' as rle_tun_cod, 0 as rle_uniact, 0 as cos_teo, sum(imp) as cos_rea,
sum(cos) as cos_reacos
from vges_etpcosnoa a, tges_etp b
where a.etp_idf_cod = b.etp_idf_cod and a.etp_emp_cod = b.etp_emp_cod and a.etp_prj_num = b.etp_prj_num
and a.etp_cod = b.etp_cod and a.etp_num = b.etp_num and dia <= nvl(b.etp_diafin,to_date('31/12/5000','dd/mm/yyyy'))
group by a.etp_idf_cod, a.etp_emp_cod, a.etp_prj_num, a.etp_cod, a.etp_num
)
)
group by idf_cod, emp_cod, prj_num, etp_cod, etp_num, uce_cod, uce_des, les_cod, les_desred,les_tun_cod,les_uniact, rle_cod,rle_des,rle_tun_cod;


