 lpr_emp_cod,
 lpr_pre_cod,
 lpr_cap_cod,
 lpr_pda_cod,
 lpr_art_cod,
 lpr_des,
 lpr_num,
 lpr_uni,
 lpr_unimet,
 lpr_uniser,
 lpr_unicmgser,
 lpr_unicmg,
