 GREATEST(lpr_uni - lpr_uniser - lpr_unicmg + lpr_unicmgser, 0) lpr_pdtcmg,
 GREATEST(lpr_unicmg - lpr_uniser, 0) lpr_cmgpdtser
FROM(
SELECT
 lpr_idf_cod,
 lpr_emp_cod,
 lpr_pre_cod,
 lpr_cap_cod,
