 lpr_emp_cod,
 lpr_pre_cod,
 lpr_cap_cod,
 lpr_pda_cod,
 lpr_art_cod,
 art_grpfac,
 lpr_grpdes lpr_des,
 lpr_art_sto,
 DECODE( art_grpfac, 'S', TO_NUMBER(NULL), lpr_num) lpr_num,
 SUM(lpr_uni) lpr_uni,
 SUM(lpr_unimet) lpr_unimet
FROM
 vges_lpresp
