CREATE OR REPLACE FORCE VIEW VGES_PENCOM
AS 
select distinct lpr_idf_cod, lpr_emp_cod, pre_cod, art_pro_cod
from
(
SELECT
lpr_idf_cod, lpr_emp_cod, pre_cli_cod,  art_pro_cod,
LPR_DIAPRTLLI, presup, PRE_COD, lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref, pre_nivurg,
pre_obspeu,
lpr_cap_cod, lpr_pda_cod, lpr_art_cod, art_grpfac,
des, lpr_lla, lpr_amp, lpr_alr, lpr_fcs, unisto, unitats,
decode(1, 1 , null,  2,  null, 3,  lpr_art_cod ||  to_char(LPR_DIAPRTLLI,'yyyymmdd') ) orden1,
decode(1, 1 ,  pre_cli_cod || presup || lpr_cap_cod || lpr_pda_cod, 2,   null, 3,
pre_cli_cod || presup || lpr_cap_cod) orden2,
decode(1, 1 , null, 2,  to_char(LPR_DIAPRTLLI,'yyyymmdd') , 3,  null) orden3,
decode(1, 1 ,   lpr_art_cod, 2,    lpr_art_cod, 3,   null) orden4,
decode(1, 1 ,  pre_obspeu, 2,  pre_obspeu, 3,   pre_obspeu) orden_pre_obspeu,
nvl(unitats,0) - nvl((select sum(lcm_uni) + sum(nvl(lcm_unigtt,0))
    from tges_lcm
   where lcm_idf_cod = '0100' and lcm_emp_cod = '0001' and
         lcm_pre_cod = pre_cod and lcm_art_cod = lpr_art_cod and
         ((lcm_cap_cod = lpr_cap_cod and lpr_cap_cod is not null) or
          (lpr_cap_cod is null )) and
         ((lcm_pda_cod = lpr_pda_cod and lpr_pda_cod is not null) or
          (lpr_pda_cod is null ))
          AND (art_grpfac='S'  OR NVL(des, chr(0)) = NVL(lcm_des, chr(0)))),0)- nvl(unisto,0) v_uni
FROM
(   -- articles composts del pressupost amb indicador d'AGRUPAR
    select lpr_idf_cod, lpr_emp_cod, pre_cli_cod, art_pro_cod, LPR_DIAPRTLLI,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver presup,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod,
	       lpr_art_cod, art_grpfac,
           decode( null, 'L', art_des,
                              art_descur ) des,
           lpr_lla, lpr_amp, lpr_alr, lpr_fcs,
           sum( lpr_unisto ) unisto,
           sum( lpr_uni ) unitats, lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref,
pre_nivurg, pre_obspeu
      from tges_lpr, tges_art, tges_pre
     where lpr_idf_cod = art_idf_cod and
           lpr_art_cod = art_cod and
		   lpr_idf_cod = pre_idf_cod and
		   lpr_emp_cod = pre_emp_cod and
		   lpr_pre_cod = pre_cod and
           nvl(art_grpfac, 'N') = 'S' and
           nvl(lpr_acc, 'N') = 'S'  and pre_est = 'A'  AND
     -- Excluir artculos con escandallo
     NOT  EXISTS
       (SELECT 1 FROM TGES_ESP
          WHERE ESP_IDF_COD = LPR_IDF_COD AND
                ESP_EMP_COD = LPR_EMP_COD AND
	 ESP_PRE_COD = LPR_PRE_COD AND
                 ESP_LPR_NUM = LPR_NUM)
     group by
	      lpr_idf_cod, lpr_emp_cod,  pre_cli_cod, art_pro_cod, art_grpfac, LPR_DIAPRTLLI,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod,
	       lpr_art_cod, lpr_pru, lpr_lla, lpr_amp, lpr_alr,
           lpr_dto, lpr_dto002, lpr_fcs,
           decode(null, 'L', art_des, art_descur ),
		   lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref,pre_nivurg, pre_obspeu
UNION ALL
    -- articles del pressupost amb indicador de FORA AGRUPAR
    select lpr_idf_cod, lpr_emp_cod, pre_cli_cod, art_pro_cod, LPR_DIAPRTLLI,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver presup,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod,
		   lpr_art_cod, art_grpfac,
           decode(null,'C', decode( 'S','S', lpr_obs,lpr_des ), lpr_des ) des,
           lpr_lla, lpr_amp, lpr_alr, lpr_fcs, lpr_uni,
           lpr_unisto unisto, lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref, pre_nivurg, pre_obspeu
      from tges_lpr, tges_art, tges_pre
     where lpr_idf_cod = art_idf_cod and lpr_art_cod = art_cod and
		   lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and
		   lpr_pre_cod = pre_cod and nvl(art_grpfac, 'N') = 'N' and
           nvl(lpr_acc, 'N') = 'S' and pre_est = 'A'    AND
    -- Excluir artculos con escandallo
     NOT  EXISTS
       (SELECT 1 FROM TGES_ESP
          WHERE
                ESP_IDF_COD = LPR_IDF_COD AND
                ESP_EMP_COD = LPR_EMP_COD AND
	 ESP_PRE_COD = LPR_PRE_COD AND
                 ESP_LPR_NUM = LPR_NUM)
UNION ALL
    -- escandalls d'articles agrupats
    select lpr_idf_cod, lpr_emp_cod, pre_cli_cod, art_pro_cod, LPR_DIAPRTLLI,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver presup,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod,
	       esp_art_cod, art_grpfac,
           decode( null, 'L', art_des,
                              art_descur ) des,
           esp_lla, esp_amp, esp_alr, esp_fcs,
           sum( esp_uni * lpr_unisto ) unisto,
           sum( esp_uni * lpr_uni ) as unitats, lpr_diaacc, pre_ope_codacc, art_tipuni,
lpr_ref, pre_nivurg, pre_obspeu
      from tges_lpr, tges_esp, tges_art, tges_pre
     where   -- ESP - ART
	       esp_idf_cod = art_idf_cod and
           esp_art_cod = art_cod and
		   -- LPR - PRE
		   lpr_idf_cod = pre_idf_cod and
		   lpr_emp_cod = pre_emp_cod and
		   lpr_pre_cod = pre_cod and
           -- ESP - LPR
           lpr_idf_cod = esp_idf_cod and
           lpr_emp_cod = esp_emp_cod and lpr_pre_cod = esp_pre_cod and
           lpr_num = esp_lpr_num and
           nvl(art_grpfac, 'N') = 'S' and
           nvl(lpr_acc, 'N') = 'S'  and pre_est = 'A'
      group by lpr_idf_cod, lpr_emp_cod, esp_art_cod, esp_lla, esp_amp, esp_alr,
              esp_fcs, LPR_DIAPRTLLI,
           decode(null, 'L', art_des, art_descur ),
		   pre_cli_cod, art_pro_cod, art_grpfac,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod, lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref,
 pre_nivurg, pre_obspeu
UNION ALL
--Encandallo de artculos no agrupados
    select lpr_idf_cod, lpr_emp_cod, pre_cli_cod, art_pro_cod, LPR_DIAPRTLLI,
	       pre_ser_cod || '/' || pre_num || '/' || pre_ver,
		   PRE_COD,
	       lpr_cap_cod, lpr_pda_cod,
	      esp_art_cod, art_grpfac, decode( null, 'S', decode( nvl( esp_obs, '^**^**^**^' ),
		  '^**^**^**^', decode( null, 'L', art_des, art_descur ), esp_obs ),
                     'N', decode(null, 'L', art_des, art_descur ) ) des,
           esp_lla, esp_amp, esp_alr,
           esp_fcs,
           esp_uni * lpr_unisto unisto,
           esp_uni * lpr_uni as unitats, lpr_diaacc, pre_ope_codacc, art_tipuni, lpr_ref,
 pre_nivurg, pre_obspeu
      from tges_lpr, tges_esp, tges_art, tges_pre
     where -- ESP - ART
	       esp_idf_cod = art_idf_cod and
           esp_art_cod = art_cod and
		   -- ESP - LPR
           lpr_idf_cod = esp_idf_cod and
           lpr_emp_cod = esp_emp_cod and lpr_pre_cod = esp_pre_cod and
           lpr_num = esp_lpr_num and
           -- LPR - PRE
		   lpr_idf_cod = pre_idf_cod and
		   lpr_emp_cod = pre_emp_cod and
		   lpr_pre_cod = pre_cod and
           --
           nvl(art_grpfac, 'N') = 'N' and
           nvl(lpr_acc, 'N') = 'S'  and pre_est = 'A')
/*where
 cli_idf_cod = lpr_idf_cod and cli_cod = pre_cli_cod and
 pro_idf_cod = '0100' and pro_cod = art_pro_cod */ )
where  v_uni > 0;


CREATE OR REPLACE FORCE VIEW VGES_PENDCOBPRE
AS 
select Tipo, idf, emp, cls, cli, ser, num, pre, Documento, dia, Impdoc, ImpPendCobro, ImpAnc 
from 
( 
  select 'FAC' Tipo, fac_idf_cod idf, fac_emp_cod emp, fac_cls cls, fac_cli_cod cli, fac_ser_cod ser, fac_num num, fac_pre_cod pre, 
  decode(fac_cls, '0', '', '**') || fac_Ser_cod || '/' || fac_num Documento, fac_dia dia, 
  fges_imptotven (fac_idf_cod, fac_emp_cod, fac_ser_cod, fac_num, fac_cls, fac_div_cod, fac_valdiveur, div_decimp) Impdoc, 
  nvl(fges_imptotven (fac_idf_cod, fac_emp_cod, fac_ser_cod, fac_num, fac_cls, fac_div_cod, fac_valdiveur, div_decimp), 0) - 
  nvl((SELECT sum(vcx_imp) from tges_vcx where vcx_idf_cod = fac_idf_cod and vcx_emp_cod = fac_emp_cod and 
   vcx_fac_cls = fac_cls and VCX_SER_COD = fac_ser_cod AND VCX_FAC_NUM = fac_num), 0) ImpPendCobro, 
   Fges_IMPANF(fac_idf_cod, fac_emp_cod, fac_ser_cod, fac_cls, fac_num, fac_div_cod, fac_valdiveur, div_decimp) ImpAnc 
