CREATE OR REPLACE FORCE VIEW VGES_PLNACC
AS 
select 'PRE' Tipo, pre_idf_cod idf, pre_emp_cod emp, pre_ser_cod || '/' || pre_num || '/' ||pre_ver Doc, pre_acc_cod acccod, pre_dom dom, pre_cpo_cod cpo 
from tges_pre 
where (pre_lat is null and pre_lon is null) 
and pre_dom is not null and pre_acc_cod is null and pre_cpo_cod is not null 
and NVL (pre_est 
, 'A') NOT IN ('C', 'N') 
union all 
select 'AVI' Tipo, avi_idf_cod idf, avi_emp_cod emp, to_char(avi_num) doc, avi_acc_cod acccod, avi_dom dom, avi_cpo_cod cpo 
from tges_avi 
where (avi_lat is null and avi_lon is null) AND NVL (Avi_EST, 1) <> 3 
and avi_dom is not null and avi_acc_cod is null and avi_cpo_cod is not null 
union all 
select 'AVR' Tipo, avr_idf_cod idf, avr_emp_cod emp, to_char(avr_num) doc, avr_acc_cod acccod, avr_dom dom, avr_cpo_cod cpo 
from tges_avr 
where (avr_lat is null and avr_lon is null) AND avr_diafin IS NULL 
and avr_dom is not null and avr_acc_cod is null and avr_cpo_cod is not null;


CREATE OR REPLACE FORCE VIEW VGES_PLNENT
AS 
SELECT   pre_idf_cod,
              pre_emp_cod,
              pre_cod,
              pre_ser_cod,
              pre_num,
              pre_ver,
              pre_nomcli,
              pre_ser_cod || '/' || pre_num || '/' || pre_ver Presupuesto,
              far_cod,
              far_des,
              far_nde,
              far_ndf,
              PRE_COMVID,
              PRE_DATCOMVID,
              PRE_COMFER,
              PRE_DATCOMFER,
              PRE_COMFOA,
              PRE_DATCOMFOA,
              PRE_COMLAC,
              PRE_DATCOMLAC,
              PRE_MON,
              PRE_DATPEVMON,
              PRE_COMPTA,
              PRE_DATCOMPTA,
              NVL (PRE_VALDATAUX, 'N') PRE_VALDATAUX,
              pre_diaacc,
              pre_diaacc + far_nde FechaMaxEntrega,
              Remesado
       FROM   tges_pre,
              (SELECT   tges_far.*,
                        fges_oneartfamrem (lpr_idf_cod,
                                           lpr_emp_cod,
                                           lpr_pre_cod,
                                           far_cod)
                           Remesado,
                        lpr_idf_cod,
                        lpr_emp_cod,
                        lpr_pre_cod
                 FROM   tges_lpr, tges_art, tges_far
                WHERE       art_idf_cod = lpr_idf_cod
                        AND art_cod = lpr_art_cod
                        AND far_idf_cod = art_idf_cod
                        AND far_cod = art_far_cod
                        AND far_nde IS NOT NULL
                        AND far_ndf IS NOT NULL)
      WHERE       lpr_idf_cod = pre_idf_cod
              AND lpr_emp_cod = pre_emp_cod
              AND lpr_pre_cod = pre_cod
              AND pre_est = 'A'
   GROUP BY   pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_ver,
              pre_nomcli, pre_ser_cod || '/' || pre_num || '/' || pre_ver, far_cod,
              far_des, far_nde, far_ndf, PRE_COMVID, PRE_COMFER, PRE_COMFOA, PRE_COMLAC,
              PRE_MON, PRE_COMPTA, nvl(PRE_VALDATAUX, 'N'), pre_diaacc, pre_diaacc + far_nde,
              PRE_DATCOMVID, PRE_DATCOMFER, PRE_DATCOMFOA, PRE_DATCOMLAC, PRE_DATPEVMON,
              PRE_DATCOMPTA, Remesado
   ORDER BY   pre_ser_cod || '/' || pre_num || '/' || pre_ver, far_des;


CREATE OR REPLACE FORCE VIEW VGES_PLNPEDREC
AS 
select   cop_idf_cod, cop_emp_cod, mag_cod,
              mag_nom, cop_tra_cod, cop_mtr_cod, cop_vol,
              to_date(to_char (cop_diaprtreb,'dd/mm/yyyy'),'dd/mm/yyyy') Fecha, count(*) Pedidos
       from   tges_cop, tges_mag
      where   mag_idf_cod = cop_idf_cod
              AND mag_cod = cop_mag_cod
              AND cop_est = 2
              and cop_mtr_cod is not null
   /*and to_date(to_char(COP_DIAPRTREB, 'dd/mm/yyyy'), 'dd/mm/yyyy') >= to_date(to_char(sysdate, 'dd/mm/yyyy'), 'dd/mm/yyyy') */
   group by cop_idf_cod, cop_emp_cod, mag_cod, mag_nom, cop_diaprtreb, cop_tra_cod, cop_mtr_cod, cop_vol;


CREATE OR REPLACE FORCE VIEW VGES_PLNWEB
AS 
SELECT   idf, emp, Tipo, Doc, Des, cli, clinom, scl, sclnom,
            Fecha, FecPrevista,
            NVL (accdom,  NVL (domscl, DOMCLI)) domcli,
            nvl (doc_lat, NVL (acclat, NVL (scl_lat, cli_lat))) lat,
            nvl (doc_lon, NVL (acclon, NVL (scl_lon, cli_lon))) lon,
            NVL (acccpo,  NVL (scl_cpo_cod, cli_cpo_cod)) cpo,
            urg, tel, percon, Planificacion, ZonaTrabajo, doc_lat, doc_lon
     FROM   (SELECT   avr_idf_cod idf,
                      avr_emp_cod emp,
                      'AVR' Tipo,
                      TO_CHAR (avr_num) Doc,
                      avr_desavr Des,
                      avr_cli_cod cli,
                      cli_nomcom clinom,
                      avr_scl_cod scl,
                      scl_nom sclnom,
                      avr_diaavi Fecha,
                      AVR_DIASOLENT FecPrevista,
                      CLI_DOMFIS DOMCLI,
                      scl_dom DOMSCL,
                      avr_dom accdom,
                      cli_lat,
                      scl_lat,
                      cli_lon,
                      scl_lon,
                      (SELECT acc_lat
                         FROM tges_acc
                        WHERE acc_idf_cod = avr_idf_cod
                              AND acc_cli_cod = avr_cli_cod
                              AND acc_cod = avr_acc_cod) acclat,
                         avr_lat doc_lat,
                      (SELECT acc_lon
                         FROM tges_acc
                        WHERE acc_idf_cod = avr_idf_cod
                              AND acc_cli_cod = avr_cli_cod
                              AND acc_cod = avr_acc_cod)
                         acclon,
                         avr_lon doc_lon,
                      avr_tel tel,
                      NVL (
                         (SELECT acc_con
                            FROM tges_acc
                           WHERE acc_idf_cod = avr_idf_cod
                                 AND acc_cli_cod = avr_cli_cod
                                 AND acc_cod = avr_acc_cod),
                         avr_nom
                      ) PerCon,
                      cli_cpo_cod,
                      scl_cpo_cod,
                      avr_cpo_cod accCpo,
                      NULL urg,
                      avr_vad_cod002 Planificacion,
                      avr_vad_cod003 ZonaTrabajo
               FROM   tges_avr, tges_cli, tges_scl
              WHERE       cli_idf_cod = avr_idf_cod
                      AND cli_cod = avr_cli_cod
                      AND scl_idf_cod(+) = avr_idf_cod
                      AND scl_cli_cod(+) = avr_cli_cod
                      AND scl_cod(+) = avr_scl_cod
                      AND avr_diafin IS NULL
             UNION ALL
             SELECT   avi_idf_cod idf,
                      avi_emp_cod emp,
                      'AVI' Tipo,
                      TO_CHAR (avi_num) Doc,
                      avi_descur Des,
                      avi_cli_cod cli,
                      cli_nomcom clinom,
                      avi_scl_cod scl,
                      scl_nom sclnom,
                      AVI_DATENT Fecha,
                      AVI_DATPRT FecPrevista,
                      CLI_DOMFIS DOMCLI,
                      scl_dom DOMSCL,
                      avi_dom accdom,
                      cli_lat,
                      scl_lat,
                      cli_lon,
                      scl_lon,
                      (SELECT   acc_lat
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         acclat,
                         avi_lat doc_lat,
                      (SELECT   acc_lon
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         acclon,
                         avi_lon doc_lon,
                      avi_tel tel,
                      (SELECT   acc_con
                         FROM   tges_acc
                        WHERE       acc_idf_cod = avi_idf_cod
                                AND acc_cli_cod = avi_cli_cod
                                AND acc_cod = avi_acc_cod)
                         percon,
                      cli_cpo_cod,
                      scl_cpo_cod,
                      avi_cpo_cod accCpo,
                      NULL urg,
                      avi_vad_cod Planificacion,
                      avi_vad_cod002 ZonaTrabajo
               FROM   tges_avi, tges_cli, tges_scl
              WHERE       cli_idf_cod = avi_idf_cod
                      AND cli_cod = avi_cli_cod
                      AND scl_idf_cod(+) = avi_idf_cod
                      AND scl_cli_cod(+) = avi_cli_cod
                      AND scl_cod(+) = avi_scl_cod
                      AND NVL (Avi_EST, 1) <> 3
             UNION ALL
             SELECT   pre_idf_cod idf,
                      pre_emp_cod emp,
                      'PRE' Tipo,
                      pre_ser_cod || '/' || pre_num || '/' || pre_ver Doc,
                      pre_ref Des,
                      pre_cli_cod cli,
                      cli_nomcom clinom,
                      pre_scl_cod scl,
                      scl_nom sclnom,
                      pre_dia Fecha,
                      PRE_DIAPRTFIN FecPrevista,
                      CLI_DOMFIS DOMCLI,
                      scl_dom DOMSCL,
                      pre_dom accdom,
                      cli_lat,
                      scl_lat,
                      cli_lon,
                      scl_lon,
                      (SELECT   acc_lat
                         FROM   tges_acc
                        WHERE       acc_idf_cod = pre_idf_cod
                                AND acc_cli_cod = pre_cli_cod
                                AND acc_cod = pre_acc_cod)
                         acclat,
                         pre_lat doc_lat,
                      (SELECT   acc_lon
                         FROM   tges_acc
                        WHERE       acc_idf_cod = pre_idf_cod
                                AND acc_cli_cod = pre_cli_cod
                                AND acc_cod = pre_acc_cod)
                         acclon,
                         pre_lon doc_lon,
                      pre_tel tel,
                      (SELECT  acc_con
                         FROM  tges_acc
                        WHERE  acc_idf_cod = pre_idf_cod
                               AND acc_cli_cod = pre_cli_cod
                               AND acc_cod = pre_acc_cod)
                         percon,
                      cli_cpo_cod,
                      scl_cpo_cod,
                      pre_cpo_cod accCpo,
                      pre_nivurg urg,
                      NULL Planificacion,
                      pre_vad_cod ZonaTrabajo
               FROM   tges_pre, tges_cli, tges_scl
              WHERE       cli_idf_cod = pre_idf_cod
                      AND cli_cod = pre_cli_cod
                      AND scl_idf_cod(+) = pre_idf_cod
                      AND scl_cli_cod(+) = pre_cli_cod
                      AND scl_cod(+) = pre_scl_cod
                      AND NVL (pre_EST, 'A') = 'A');


CREATE OR REPLACE FORCE VIEW VGES_PREAVI
AS 
select PRE_IDF_COD, PRE_EMP_COD, PRE_COD,
 PRE_SER_COD, PRE_NUM, PRE_VER, PRE_SERNUMVER,
 PRE_REF, PRE_CLI_COD, CLI_NOMCOM, PRE_SCL_COD, PRE_ACC_COD, ZONACLI, PRE_PRJ_NUM, PRE_OPE_COD,
 DES, UNI, LPR_CAP_COD, LPR_PDA_COD,
 OPE_CODDES, TIPAVI, SECCIO, EMAIL
from
(select pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_Ver,
pre_ser_cod || '/' || pre_num || '/' || pre_ver as pre_sernumver,
pre_ref, pre_cli_cod, cli_nomcom, pre_scl_cod, pre_acc_cod, cli_zon_cod as zonacli, pre_prj_num, pre_ope_cod, lpr_des as des,
sum(lpr_uni) as uni, lpr_cap_cod, lpr_pda_cod,
art_partxt004 as ope_coddes, art_partxt005 as tipavi, FRHU_SEC_OPE(pre_idf_cod, art_partxt004, 'C') as Seccio,
ope_eml as email
from tges_pre, tges_lpr, tges_art, tges_far, tges_cli, trhu_ope
where lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and lpr_pre_cod = pre_cod
and cli_idf_cod = pre_idf_cod and cli_cod = pre_cli_cod
and art_idf_cod = lpr_idf_cod and art_cod = lpr_art_cod and art_cst = 'N'
and far_idf_cod = art_idf_cod and  far_cod = art_far_cod and far_tip = 'O' and lpr_uni <> 0 
and pre_est = 'A' and lpr_acc = 'S' 
and ope_idf_cod = pre_idf_cod and ope_cod = art_partxt004
group by pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_Ver,
pre_ser_cod || '/' || pre_num || '/' || pre_ver, pre_ref, pre_cli_cod, cli_nomcom, pre_scl_cod, pre_acc_cod, cli_zon_cod, pre_prj_num, pre_ope_cod,
lpr_des, lpr_cap_cod, lpr_pda_cod, art_partxt004, art_partxt005, ope_eml
union all
select pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_Ver,
pre_ser_cod || '/' || pre_num || '/' || pre_ver as pre_sernumver,
pre_ref, pre_cli_cod, cli_nomcom, pre_scl_cod, pre_acc_cod, cli_zon_cod as zonacli, pre_prj_num, pre_ope_cod,
lpr_Des as des, sum(nvl(lpr_uni * esp_uni, 0)) as uni, lpr_cap_cod, lpr_pda_cod,
art2.art_partxt004 as ope_coddes, art2.art_partxt005 as tipavi, FRHU_SEC_OPE(pre_idf_cod, art2.art_partxt004, 'C') as Seccio,
ope_eml as email
from tges_pre, tges_lpr, tges_esp, tges_art art1, tges_far far1, tges_art art2, tges_far far2, tges_cli, trhu_ope
where lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and lpr_pre_cod = pre_cod and
      ope_idf_cod = pre_idf_cod and ope_cod = art2.art_partxt004 and
      cli_idf_cod = pre_idf_cod and cli_cod = pre_cli_cod and
      --
      art1.art_idf_cod = lpr_idf_cod and art1.art_cod = lpr_art_cod and art1.art_cst = 'S' and
      --
      far1.far_idf_cod = art1.art_idf_cod and far1.far_cod = art1.art_far_cod and far1.far_tip = 'O' and
      --
      esp_idf_cod = lpr_idf_cod and esp_emp_cod = lpr_emp_cod and esp_pre_cod = lpr_pre_cod and esp_lpr_num = lpr_num and
      --
      art2.art_idf_cod = esp_idf_cod and art2.art_cod = esp_art_cod and
      --
      far2.far_idf_cod = art2.art_idf_cod and far2.far_cod = art2.art_far_cod and far2.far_tip = 'O' and
	  --
      lpr_uni <> 0 and esp_uni <> 0  and pre_est = 'A' and lpr_acc = 'S'
group by pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_Ver,
pre_ser_cod || '/' || pre_num || '/' || pre_ver, pre_ref, pre_cli_cod, cli_nomcom, pre_scl_cod, pre_acc_cod, cli_zon_cod, pre_prj_num, pre_ope_cod,
lpr_des, lpr_cap_cod, lpr_pda_cod, art2.art_partxt004, art2.art_partxt005, ope_eml
);


CREATE OR REPLACE FORCE VIEW VGES_PREFAR
AS 
select distinct pre_idf_cod IDF, pre_emp_cod EMPRESA, pre_ser_cod SERIE, pre_num NUMERO, 
pre_ver VERSION, decode(pre_est, 'A', 'ACEPTADO', 'P', 'PENDIENTE','C','CERRADO','N','NO ACEPTADO','E','ACEPTADO CLIENTE','Z','APLAZADO') ESTADO, 
pre_cli_cod CLIENTE, pre_nomcli NOMBRE_CLIENTE, 
pre_ref REFERENCIA, pre_dia FECHA_PRESUPUESTO, pre_diaacc FECHA_ACEPTACION, pre_diaini FECHA_INICIO, 
pre_diaprtfin FECHA_FINPREVISTO, pre_diatan FECHA_CIERRE, far_cod FAMILIA, far_des NOMBRE_FAMILIA 
from tges_pre, tges_lpr, tges_art,tges_far 
where 
lpr_idf_cod = pre_idf_cod and lpr_emp_cod = pre_emp_cod and lpr_pre_cod = pre_cod 
and lpr_idf_cod = art_idf_cod and lpr_art_cod = art_cod 
and art_idf_cod = far_idf_cod and art_far_cod = far_cod;


