CREATE OR REPLACE FORCE VIEW VGES_PREIMPALB
AS 
select pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_ver, 
lac_cap_cod, nvl(cap_seq,'0') cap_seq, nvl(cap_des,'SIN CAPITULO') cap_des, 
lac_pda_cod, nvl(pda_seq,'0') pda_seq, nvl(pda_des,'SIN PARTIDA') pda_des, 
round(sum(lac_imp / alb_valdiveur),2) pad_imp 
from tges_pre, tges_lac, tges_cap, tges_pda, tges_alb 
where 
lac_idf_cod = pre_idf_cod and lac_emp_cod = pre_emp_cod and lac_pre_cod = pre_cod 
and cap_idf_cod(+) = lac_idf_cod and cap_emp_cod(+) = lac_emp_cod and cap_pre_cod(+) = lac_pre_cod and cap_cod(+) = lac_cap_cod 
and pda_idf_cod(+) = lac_idf_cod and pda_emp_cod(+) = lac_emp_cod and pda_pre_cod(+) = lac_pre_cod and pda_cap_cod(+) = lac_cap_cod and pda_cod(+) = lac_pda_cod 
and alb_idf_cod = lac_idf_cod and alb_emp_cod = lac_emp_cod and alb_numdoc = lac_alb_numdoc 
group by pre_idf_cod, pre_emp_cod, pre_cod, pre_ser_cod, pre_num, pre_ver, 
lac_cap_cod, cap_seq, cap_des, 
lac_pda_cod, pda_seq, pda_des 
order by pre_idf_cod, pre_emp_cod,pre_ser_cod, pre_num, pre_ver, cap_seq, pda_seq;


CREATE OR REPLACE FORCE VIEW VGES_PREPRJESP
AS 
select idf_cod, emp_cod,
       pre_cod, cap_cod, pda_cod, lpr_num, esp_num,
	   art_cod, art_grpfac, art_far_cod,far_tip,far_fct_cod,lpr_des,
	   pru, fcs, acc, pre_est,
	   uni, unimet, imp, cos,
       div_cod, div_valeur, mag_cod,
      Presup,
      pre_cli_cod,
      pre_nomcli,
      pre_scl_cod,
--	  pre_dte,
--	  pre_dte002,
	  prjnum
from
(
 ( select esp_idf_cod idf_cod, esp_emp_cod emp_cod,
          esp_pre_cod pre_cod, lpr_cap_cod cap_cod,lpr_pda_cod pda_cod, esp_lpr_num lpr_num, esp_num esp_num,
          esp_art_cod art_cod, nvl( art_grpfac, 'N' ) art_grpfac,art_far_cod,far_tip,far_fct_cod, art_cst,
          art_descur lpr_des,
          esp_pvp pru, esp_fcs * lpr_fcs fcs, nvl( lpr_acc, 'N' ) acc,
          nvl( pre_est, 'P' ) pre_est, art_sto,
          lpr_uni * esp_uni uni, lpr_uni * esp_unimet unimet,
          lpr_uni * esp_unimet * esp_pvp imp, lpr_uni * esp_unimet * esp_cos cos,
          pre_div_cod div_cod, pre_valdiveur div_valeur,
          pre_mag_cod mag_cod,
          pre_ser_cod || ''/'' || pre_num || ''/'' || pre_ver Presup,
          pre_cli_cod,
          pre_nomcli,
          pre_scl_cod,
	--	  pre_dte,
	--	  pre_dte002,
		  pjp_prj_num as prjnum
     from tges_pre, tges_lpr, tges_esp, tges_art, tges_far, tges_pjp
    where lpr_idf_cod = pre_idf_cod and
          lpr_emp_cod = pre_emp_cod and
          lpr_pre_cod = pre_cod     and
          esp_idf_cod = lpr_idf_cod and
          esp_emp_cod = lpr_emp_cod and
          esp_pre_cod = lpr_pre_cod and
          esp_lpr_num = lpr_num     and
          esp_idf_cod = art_idf_cod and
          esp_art_cod = art_cod     and
	      far_idf_cod = art_idf_cod and
		  far_cod     = art_far_cod and
  lpr_idf_cod = pjp_idf_cod(+) and
 lpr_emp_cod = pjp_emp_cod(+) and
 lpr_pre_cod = pjp_pre_cod(+) and
 nvl(lpr_cap_cod, '?') = nvl(pjp_cap_cod, nvl(lpr_cap_cod, '?')) and
 nvl(lpr_pda_cod, '?') = nvl(pjp_pda_cod, nvl(lpr_pda_cod, '?'))
 )
);


CREATE OR REPLACE FORCE VIEW VGES_PRJALB
AS 
select alb_idf_cod,alb_emp_cod,alb_cli_cod,alb_prj_num,prj_nom, 
nvl(prj_div_cod,alb_div_cod)as div_cod,alb_cls,alb_ser_cod,alb_num,alb_numdoc,alb_dia, to_char(alb_dia,'YYYY') as alb_any,to_char(alb_dia,'MM') as alb_mes, 
lac_art_cod,lac_unimet,fges_canvi_divisa(alb_idf_cod,lac_pru,alb_div_cod,nvl(prj_div_cod,alb_div_cod)) as lac_pru, 
lac_dto,lac_dto002, 
fges_canvi_divisa(alb_idf_cod,lac_imp ,alb_div_cod,nvl(prj_div_cod,alb_div_cod)) as lac_imp, 
lap_alb_numdoc,lap_num 
from tges_alb,tges_lac,tges_prj,tges_lap 
where 
lac_idf_cod = alb_idf_cod and lac_emp_cod = alb_emp_cod and lac_alb_numdoc = alb_numdoc 
and  alb_idf_cod = prj_idf_cod(+) and alb_emp_cod = prj_emp_cod(+) and alb_prj_num = prj_num(+) 
and lac_idf_cod = lap_idf_cod(+) and lac_emp_cod = lap_emp_cod(+) and lac_alb_numdoc = lap_alb_numdoc(+) 
and lac_num = lap_lac_num(+);


CREATE OR REPLACE FORCE VIEW VGES_PRJALP
AS 
select alp_idf_cod,alp_emp_cod,alp_pro_cod,alp_prj_num,prj_nom,nvl(alp_magprj,'N') as alp_magprj, 
