CREATE OR REPLACE FORCE VIEW VGES_RELVEN
AS 
SELECT                                                         /*+ RULE*/
           fac_cls,
              fac_ser_cod,
              fac_num,
              ser_des,
              fac_dia,
              TO_CHAR (fac_dia, 'yyyy') anyo,
              TO_CHAR (fac_dia, 'mm') mes,
              TO_CHAR (fac_dia, 'dd') dia,
              far_des,
              DECODE (art_gma_cod, NULL, 'SIN SUBFAMILIA', gma_des) gma_des,
              art_descur,
              DECODE (fac_ser_cod,
                      NULL, alb_ser_cod || '/' || alb_num,
                      fac_ser_cod || '/' || fac_num)
                 factura,
              iva_pte,
              Fges_Fac_Pdtcob (fac_idf_cod,
                               fac_emp_cod,
                               fac_ser_cod,
                               fac_num,
                               fac_cls)
                 pdtcob,
              lac_uni lac_uni,
              DECODE (
                 lac_uni,
                 0,
                 0,
                 ROUND (
                    ROUND (
                         lac_imp
                       * (1 - NVL (fac_dto, 0) * 0.01)
                       * (1 - NVL (fac_dto002, 0) * 0.01),
                       2
                    )
                    / lac_uni,
                    2
                 )
              )
                 lac_pru,
              ROUND (
                   lac_imp
                 * (1 - NVL (fac_dto, 0) * 0.01)
                 * (1 - NVL (fac_dto002, 0) * 0.01),
                 2
              )
                 lac_imp,
              (SELECT   MAX (DPG_DES)
                 FROM   tges_vcx, tges_mdc, tges_dpg
                WHERE       vcx_idf_cod = fac_idf_cod
                        AND vcx_emp_cod = fac_emp_cod
                        AND vcx_fac_cls = fac_cls
                        AND vcx_fac_num = fac_num
                        AND vcx_ser_cod = fac_ser_cod
                        AND vcx_idf_cod = mdc_idf_cod
                        AND vcx_emp_cod = mdc_emp_cod
                        AND vcx_cxa_cod = mdc_cxa_cod
                        AND vcx_mdc_num = mdc_num
                        AND DPG_IDF_COD = MDC_IDF_COD
                        AND DPG_COD = MDC_DPG_COD)
                 fpg_des,
              alb_dia,
              alb_ser_cod,
              art_far_cod,
              art_gma_cod,
              lac_art_cod,
              alb_cls,
              alb_idf_cod,
              alb_emp_cod
       FROM   tges_alb,
              tges_lac,
              tges_fac,
              tges_art,
              tges_ser,
              tges_far,
              tges_gma,
              tges_iva
      WHERE       lac_iva_cod(+) = iva_cod
              AND lac_idf_cod(+) = iva_idf_cod
              AND far_idf_cod = art_idf_cod
              AND far_cod = art_far_cod
              AND art_idf_cod = gma_idf_cod(+)
              AND art_gma_cod = gma_cod(+)
              AND SER_IDF_COD = alb_idf_cod
              AND SER_EMP_COD = alb_emp_cod
              AND SER_COD = alb_ser_cod
              AND ( (    lac_alb_numdoc = alb_numdoc
                     AND lac_emp_cod = alb_emp_cod
                     AND lac_idf_cod = alb_idf_cod)
                   AND (    alb_fac_num = fac_num(+)
                        AND alb_fac_cls = fac_cls(+)
                        AND alb_ser_codfac = fac_ser_cod(+)
                        AND alb_emp_cod = fac_emp_cod(+)
                        AND alb_idf_cod = fac_idf_cod(+))
                   AND (lac_art_cod = art_cod AND lac_idf_cod = art_idf_cod))
   ORDER BY   fac_dia;


CREATE OR REPLACE FORCE VIEW VGES_STO
AS 
SELECT
    STO_IDF_COD,
    STO_MAG_COD,
    STO_ART_COD,
    STO_PMG_COD,
    STO_TIP,
    NVL(STO_UNIINI,0)    STO_UNIINI,      -- Unidades iniciales
    NVL(STO_UNIINI002,0) STO_UNIINI002,   -- Unidades iniciales
    NVL(STO_VALINI,0)    STO_VALINI,      -- Valor de unidades iniciales
    NVL(STO_VALINI002,0) STO_VALINI002,   -- Valor de unidades iniciales
    NVL(STO_UNIRSV,0)    STO_UNIRSV,
    NVL(STO_UNIRSV002,0) STO_UNIRSV002,
    NVL(STO_UNIPENREB,0) STO_UNIPENREB,
    NVL(STO_UNICOMCLI,0) STO_UNICOMCLI,
      nvl(STO_UNIINI,0)         -- Unidades iniciales
    + nvl(STO_UNICPRPRO,0)      -- Unidades compradas a proveedores
    + nvl(STO_UNIENTALTMAG,0)   -- Unidades entradas otros almacenes
    + nvl(STO_UNIENTINV,0)      -- Unidades entradas por inventarios
    + nvl(STO_UNIDIPPRO,0)      -- Unidades en depsito de proveedores
    + nvl(STO_UNIFAB,0)         -- Unidades fabricadas
    AS STO_UNIENT,
      nvl(STO_UNISORALB,0)      -- Unidades salidas por albaranes
    + nvl(STO_UNISORMAG,0)      -- Unidades salidas a almacenes
    + nvl(STO_UNISORINV,0)      -- Unidades salidas por inventarios
    + nvl(STO_UNIDIPCLI,0)      -- Unidades en deposito de clientes
    + nvl(STO_UNISORFAB,0)      -- Unidades salidas por fabricacion
    + nvl(STO_UNIDEF,0)		-- Unidades defectuosas
    AS STO_UNISOR,
      nvl(STO_VALINI,0)         -- Unidades iniciales
    + nvl(STO_VALCPRPRO,0)      -- Unidades compradas a proveedores
    + nvl(STO_VALENTALTMAG,0)   -- Unidades entradas otros almacenes
    + nvl(STO_VALENTINV,0)      -- Unidades entradas por inventarios
    + nvl(STO_VALDIPPRO,0)      -- Unidades en depsito de proveedores
    + nvl(STO_VALFAB,0)         -- Unidades fabricadas
    AS STO_VALENT,
      nvl(STO_VALSORALB,0)      -- Unidades salidas por albaranes
    + nvl(STO_VALSORMAG,0)      -- Unidades salidas a almacenes
    + nvl(STO_VALSORINV,0)      -- Unidades salidas por inventarios
    + nvl(STO_VALDIPCLI,0)      -- Unidades en deposito de clientes
    + nvl(STO_VALSORFAB,0)      -- Unidades salidas por fabricacion
    + nvl(STO_VALDEF,0)		-- Unidades defectuosas
    AS STO_VALSOR,
      nvl(STO_UNIINI002,0)         -- Unidades iniciales
    + nvl(STO_UNICPRPRO002,0)      -- Unidades compradas a proveedores
    + nvl(STO_UNIENTALTMAG002,0)   -- Unidades entradas otros almacenes
    + nvl(STO_UNIENTINV002,0)      -- Unidades entradas por inventarios
    /* + nvl(STO_UNIDIPPRO,0)      -- Unidades en depsito de proveedores*/
    + nvl(STO_UNIFAB002,0)         -- Unidades fabricadas
    AS STO_UNIENT002,
      nvl(STO_UNISORALB002,0)   -- Unidades salidas por albaranes
    + nvl(STO_UNISORALTMAG002,0)-- Unidades salidas a almacenes
    + nvl(STO_UNISORINV002,0)   -- Unidades salidas por inventarios
    /* + nvl(STO_UNIDIPCLI,0)      -- Unidades en deposito de clientes*/
    + nvl(STO_UNISORFAB002,0)      -- Unidades salidas por fabricacion
    + nvl(STO_UNIDEF002,0)	-- Unidades defectuosas
    AS STO_UNISOR002,
      nvl(STO_VALINI002,0)         -- Unidades iniciales
    + nvl(STO_VALCPRPRO002,0)      -- Unidades compradas a proveedores
    + nvl(STO_VALENTALTMAG002,0)   -- Unidades entradas otros almacenes
    + nvl(STO_VALENTINV002,0)      -- Unidades entradas por inventarios
    /* + nvl(STO_VALDIPPRO,0)      -- Unidades en depsito de proveedores*/
    + nvl(STO_VALFAB002,0)         -- Unidades fabricadas
    AS STO_VALENT002,
      nvl(STO_VALSORALB002,0)   -- Unidades salidas por albaranes
    + nvl(STO_VALSORALTMAG002,0)-- Unidades salidas a almacenes
    + nvl(STO_VALSORINV002,0)   -- Unidades salidas por inventarios
    /* + nvl(STO_VALDIPCLI,0)      -- Unidades en deposito de clientes*/
    + nvl(STO_VALSORFAB002,0)      -- Unidades salidas por fabricacion
    + nvl(STO_VALDEF002,0)	-- Unidades defectuosas
    AS STO_VALSOR002
  FROM
    TGES_STO;


CREATE OR REPLACE FORCE VIEW VGES_STOEXI
AS 
SELECT
    STO_IDF_COD,
    STO_MAG_COD,
    STO_ART_COD,
    STO_PMG_COD,
    STO_TIP,
    STO_UNIPENREB,
    STO_UNIENT,
    STO_UNISOR,
    STO_VALENT,
    STO_VALSOR,
    STO_UNIENT002,
    STO_UNISOR002,
    STO_VALENT002,
    STO_VALSOR002,
    STO_UNIENT - STO_UNISOR STO_EXI,
    STO_UNIENT002 - STO_UNISOR002 STO_EXI002,
    STO_UNIENT + STO_UNIENT002 - (STO_UNISOR + STO_UNISOR002) STO_EXITOT
  FROM
    VGES_STO;


CREATE OR REPLACE FORCE VIEW VGES_STOEXIACT
AS 
select  sto_idf_cod as idf, art_cod as art, art_descur as des , sto_mag_cod as mag, 
        ( sum(nvl(sto_uniini,0)+nvl(sto_uniini002,0)) 
        + sum(nvl(sto_unicprpro,0)+nvl(sto_unicprpro002,0)) 
        + sum(nvl(sto_unientaltmag,0)+nvl(sto_unientaltmag002,0)) 
        + sum(nvl(sto_unientinv,0)+nvl(sto_unientinv002,0)) 
        + sum(nvl(sto_unidippro,0)) 
        + sum(nvl(sto_unifab,0) + nvl(sto_unifab002,0)) 
        - sum(nvl(sto_unisoralb,0)+nvl(sto_unisoralb002,0)) 
        - sum(nvl(sto_unisormag,0)+nvl(sto_unisoraltmag002,0)) 
        - sum(nvl(sto_unisorinv,0)+nvl(sto_unisorinv002,0)) 
        - sum(nvl(sto_unidipcli,0)) 
        - sum(nvl(sto_unisorfab,0) + nvl(sto_unisorfab002,0)) 
        - sum(nvl(sto_unidef,0)+nvl(sto_unidef002,0)) ) as uniexi, 
        sum(nvl(sto_unipenreb,0)) as unipenreb, 
        sum(nvl(sto_unicomcli,0)) as unicomcli, 
        sum(nvl(sto_unirsv,0)+nvl(sto_unirsv002,0)) as unirsv 
from tges_sto,tges_art 
where sto_tip='1' 
and sto_pmg_cod= 
(select pmg_cod from tges_pmg pmg1 
     where pmg_diaini <= sysdate and pmg_diaini < 
(select nvl(min(pmg2.pmg_diaini), '31/12/3000') 
   from tges_pmg pmg2 
  where pmg2.pmg_diaini > sysdate and 
        pmg2.pmg_idf_cod=pmg1.pmg_idf_cod and 
        pmg2.pmg_mag_cod = pmg1.pmg_mag_cod) 
and pmg_diaini >= 
(select nvl(max(pmg3.pmg_diaini), '01/01/1900') 
   from tges_pmg pmg3 
  where pmg3.pmg_diaini <= sysdate and 
        pmg3.pmg_idf_cod=pmg1.pmg_idf_cod and 
        pmg3.pmg_mag_cod = pmg1.pmg_mag_cod) 
and pmg_idf_cod = sto_idf_cod and pmg_mag_cod = sto_mag_cod) 
and sto_idf_cod = art_idf_cod and sto_art_cod = art_cod 
group by sto_idf_cod, art_cod,art_descur,sto_mag_cod;


