max(rle_des) rle_des,sum (rle_uni * (les_unietd - nvl(les_uniact,0) - nvl(les_uniant,0) ) )  SUM_UNI, 
sum(rle_uni * (les_unietd - nvl(les_uniact,0) - nvl(les_uniant,0)) * rle_cosuni) SUM_COSUNI, 
sum (rle_uni * ( nvl(les_uniact,0) + nvl(les_uniant,0) ) )  SUM_UNIORI, 
sum(rle_uni * ( nvl(les_uniact,0) + nvl(les_uniant,0) )* rle_cosuni) SUM_COSORI, 
sum (rle_uni * ( nvl(les_uniact,0)  ) )  SUM_UNIACT, 
sum(rle_uni * ( nvl(les_uniact,0)  )* rle_cosuni) SUM_COSACT 
from tges_rle, tges_les, tges_etp,tges_uce,tges_prj 
where 
rle_idf_cod = les_idf_cod and rle_emp_cod = les_emp_cod and rle_prj_num = les_prj_num 
and rle_etp_cod = les_etp_cod and rle_etp_num = les_etp_num and rle_les_seq = les_seq 
and rle_idf_cod = etp_idf_cod and rle_emp_cod = etp_emp_cod and rle_prj_num = etp_prj_num 
and rle_etp_cod = etp_cod and rle_etp_num = etp_num 
and les_idf_cod = uce_idf_cod and les_emp_cod = uce_emp_cod and les_prj_num = uce_prj_num 
and les_etp_cod = uce_etp_cod and les_etp_num = uce_etp_num and les_uce_seq = uce_seq 
and rle_idf_cod = prj_idf_cod and rle_emp_cod = prj_emp_cod and rle_prj_num = prj_num 
group by rle_idf_cod, rle_emp_cod, rle_prj_num, prj_nom,etp_valdiveur,rle_etp_cod,rle_etp_num,etp_des,uce_cod, uce_des,rle_tun_cod, 
rle_cod,upper(replace(rle_des,' ',''));


CREATE OR REPLACE FORCE VIEW VGES_UCERLECOS
AS 
(
select 'T' as tip,rle_idf_cod as idf_cod, rle_emp_cod as emp_cod, rle_prj_num as prj_num , rle_etp_cod as etp_cod,
rle_etp_num as etp_num , uce_cod as uce_cod , uce_des as uce_des , rle_cod as rle_cod,
sum(sum_uniact) as uni_teo, sum(sum_cosact) as cos_teo, sum(sum_uniori) as uni_teoori, sum(sum_cosori) as cos_teoori, 0 as uni_rea, 0 as cos_rea,0 as cos_reacos
from vges_ucereccod
group by rle_idf_cod, rle_emp_cod, rle_prj_num, rle_etp_cod, rle_etp_num, uce_cod,uce_des,rle_cod
)
union all
(
select 'R' as tip,a.etp_idf_cod as idf_cod ,a.etp_emp_cod as emp_cod, a.etp_prj_num as prj_num,a.etp_cod as etp_cod,
a.etp_num as etp_num, uce_cod as uce_cod , uce_des uce_des , nvl(rle_cod,'ZZ-Sin Recurso') as rle_cod ,
0 as uni_teo, 0 as cos_teo, 0 as uni_teoori, 0 as cos_teoori, sum(uni) as uni_rea ,sum(imp) as cos_rea, sum(cos) as cos_reacos
from vges_ucecosrea a , tges_etp b
where a.etp_idf_cod = b.etp_idf_cod and a.etp_emp_cod = b.etp_emp_cod and a.etp_prj_num = b.etp_prj_num
and a.etp_cod = b.etp_cod and a.etp_num = b.etp_num and dia <= nvl(b.etp_diafin,to_date('31/12/5000','dd/mm/yyyy'))
group by a.etp_idf_cod,a.etp_emp_cod, a.etp_prj_num, a.etp_cod, a.etp_num, uce_cod,uce_des, rle_cod
)
union all
(
select 'N' as tip,a.etp_idf_cod as idf_cod , a.etp_emp_cod as emp_cod , a.etp_prj_num as prj_num, a.etp_cod as etp_cod,
a.etp_num as etp_num,  'Sin Asignar' as uce_cod, 'Sin Asignar' as uce_des, 'ZZ-Sin Recurso' as rle_cod,
0 as uni_teo, 0 as cos_teo,  0 as uni_teoori, 0 as cos_teoori, sum(uni) as uni_rea ,sum(imp) as cos_rea, sum(cos) as cos_reacos
from vges_etpcosnoa a, tges_etp b
where a.etp_idf_cod = b.etp_idf_cod and a.etp_emp_cod = b.etp_emp_cod and a.etp_prj_num = b.etp_prj_num
and a.etp_cod = b.etp_cod and a.etp_num = b.etp_num and dia <= nvl(b.etp_diafin,to_date('31/12/5000','dd/mm/yyyy'))
group by a.etp_idf_cod, a.etp_emp_cod, a.etp_prj_num, a.etp_cod, a.etp_num
);


CREATE OR REPLACE FORCE VIEW VGES_UNIENVEML
AS 
select idf, emp, cop, art, sum(uniCMD) uni_cmd, sum(uniRBD) uni_rbd 
FROM 
( SELECT lap_idf_cod idf, lap_emp_cod emp, lap_cop_cod cop, lap_art_cod art, 0 uniCMD, sum(lap_uni) as uniRBD 
  FROM   tges_lap where lap_cop_cod is not null 
  group by lap_idf_cod, lap_emp_cod, lap_cop_cod, lap_art_cod 
  UNION ALL 
  SELECT lcm_idf_cod idf, lcm_emp_cod emp, lcm_cop_num cop, lcm_art_cod art, sum(lcm_uni) as uniCMD, 0 uniRBD 
  FROM   tges_lcm 
  group by lcm_idf_cod, lcm_emp_cod, lcm_cop_num, lcm_art_cod 
) v1 
GROUP BY idf, emp, cop, art;


