CREATE OR REPLACE FORCE VIEW VRHU_ALB_ALE
AS 
SELECT   IDF,
              EMP,
              DATA,
              CODIOPERARI,
              NOMOPERARI,
              TIPUSHORA,
              TEMPS,
              CODICLIENT,
              NOMCLIENT,
              ALBARA,
              CLASSE,
              FACTURA,
              PRESSUPOST,
              VEHICLE,
              KM,
              SECCIO,
              NOMSECCIO,
              REGIMEN,
              pre_ser_cod,
              pre_num,
              pre_ver,
              alb_ser_cod,
              alb_num,
              fac_ser_cod,
              fac_num,
              DECODE (fac_cer_num, NULL, 0, 1) AS facturaCertificacio,
              pre_cod,
              alb_numdoc,
              projecte
       FROM   (  SELECT   inr_idf_cod IDF,
                          NULL EMP,
                          inr_cln_dat DATA,
                          ope_cod CODIOPERARI,
                          ope_nom NOMOPERARI,
                          'M' TIPUSHORA,
                          ROUND (SUM ( (inr_fin - inr_ini) * 24), 2) TEMPS,
                          NULL CODICLIENT,
                          NULL NOMCLIENT,
                          NULL ALBARA,
                          NULL CLASSE,
                          NULL FACTURA,
                          NULL PRESSUPOST,
                          NULL VEHICLE,
                          0 KM,
                          frhu_sec_ope (ope_idf_cod,
                                        ope_cod,
                                        'C',
                                        inr_cln_dat)
                             SECCIO,
                          frhu_sec_ope (ope_idf_cod,
                                        ope_cod,
                                        'D',
                                        inr_cln_dat)
                             NOMSECCIO,
                          FRHU_RDIREG (ope_idf_cod, ope_cod, inr_cln_dat) REGIMEN,
                          TO_CHAR (NULL) AS pre_ser_cod,
                          TO_NUMBER (NULL) AS pre_num,
                          TO_NUMBER (NULL) AS pre_ver,
                          TO_CHAR (NULL) AS alb_ser_cod,
                          TO_NUMBER (NULL) AS alb_num,
                          TO_CHAR (NULL) fac_ser_cod,
                          TO_NUMBER (NULL) fac_num,
                          TO_NUMBER (NULL) fac_cer_num,
                          TO_NUMBER (NULL) pre_cod,
                          TO_NUMBER (NULL) AS alb_numdoc,
                          TO_CHAR (NULL) AS projecte
                   FROM   trhu_inr, trhu_ope
                  WHERE       inr_idf_cod = ope_idf_cod
                          AND inr_ope_cod = ope_cod
                          AND ROUND ( (inr_fin - inr_ini) * 24, 2) <> 0
               GROUP BY   inr_idf_cod,
                          inr_cln_dat,
                          ope_idf_cod,
                          ope_cod,
                          ope_nom
               UNION ALL
               --
               SELECT   lff_idf_cod IDF,
                        lff_emp_cod EMP,
                        ffo_cln_dat DATA,
                        ffo_ope_cod CODIOPERARI,
                        ope_nom NOMOPERARI,
                        'P' TIPUSHORA,
                        ROUND (NVL (lff_hor, 0) + (NVL (lff_min, 0) / 60), 2)
                           TEMPS,
                        NVL (alb_cli_cod, lff_cli_cod) CODICLIENT,
                        cli_nomcom NOMCLIENT,
                        DECODE (alb_num,
                                NULL, NULL,
                                alb_ser_cod || '\' || alb_num)
                           ALBARA,
                        alb_cls CLASSE,
                        DECODE (fac_num,
                                NULL, NULL,
                                fac_ser_cod || '\' || fac_num)
                           FACTURA,
                        DECODE (pre_num,
                                NULL, NULL,
                                pre_ser_cod || '\' || pre_num)
                           PRESSUPOST,
                        NULL VEHICLE,
                        0 KM,
                        frhu_sec_ope (lff_idf_cod,
                                      ffo_ope_cod,
                                      'C',
                                      ffo_cln_dat)
                           SECCIO,
                        frhu_sec_ope (lff_idf_cod,
                                      ffo_ope_cod,
                                      'D',
                                      ffo_cln_dat)
                           NOMSECCIO,
                        FRHU_RDIREG (lff_idf_cod, ffo_ope_cod, ffo_cln_dat)
                           REGIMEN,
                        pre_ser_cod,
                        pre_num,
                        pre_ver,
                        alb_ser_cod,
                        alb_num,
                        fac_ser_cod,
                        fac_num,
                        fac_cer_num,
                        pre_cod,
                        alb_numdoc,
