                        alb_dia DATA,
                        ale_ope_cod CODIOPERARI,
                        ope_nom NOMOPERARI,
                        'D' TIPUSHORA,
                        ROUND (
                           NVL (ale_hordpl, 0) + (NVL (ale_mindpl, 0) / 60),
                           2
