                        ale_kmt KM,
                        frhu_sec_ope (ale_idf_cod,
                                      ale_ope_cod,
                                      'C',
                                      alb_dia)
                           SECCIO,
                        frhu_sec_ope (ale_idf_cod,
                                      ale_ope_cod,
                                      'D',
                                      alb_dia)
                           NOMSECCIO,
                        FRHU_RDIREG (ale_idf_cod, ale_ope_cod, alb_dia) REGIMEN,
                        pre_ser_cod,
                        pre_num,
                        pre_ver,
                        alb_ser_cod,
                        alb_num,
                        fac_ser_cod,
                        fac_num,
                        fac_cer_num,
                        pre_cod,
                        alb_numdoc,
                        alb_prj_num AS projecte
                 FROM   tges_ale,
                        tges_alb,
                        tges_cli,
                        tges_pre,
                        tges_mtr,
                        trhu_ope,
                        tges_fac
                WHERE       alb_idf_cod = ale_idf_cod
