CREATE OR REPLACE FORCE VIEW VGES_CAPGRUP
AS 
select
OFR_OPE_CODCGR CAP_GRUP, o1.OPE_NOM NOM_CAPGRUP
, OFR_OPE_CODCAO CAP_OBRA, o2.OPE_NOM NOM_CAPOBRA
, FGES_PJP_OFR(ofr_idf_cod, ofr_emp_cod, ofr_numofr) CODI_OBRA, ofr_ttlobr OBRA
, ofr_tipobr TIPOLOGIA
, ofr_cpx COJUNTURA
, ROUND(NVL(ofr_tipobr,0)*NVL(ofr_cpx,0), 2) PONDERACIO
from tges_ofr, TRHU_OPE o1,TRHU_OPE o2
where OFR_IDF_COD = o1.OPE_IDF_COD(+)
AND OFR_OPE_CODCGR = o1.OPE_COD(+)
AND OFR_IDF_COD = o2.OPE_IDF_COD(+)
AND OFR_OPE_CODCAO = o2.OPE_COD(+);


CREATE OR REPLACE FORCE VIEW VGES_CAPOBRA
AS 
select OFR_OPE_CODCAO CAP_OBRA, OPE_NOM NOM_CAPOBRA, FGES_PJP_OFR(ofr_idf_cod, ofr_emp_cod, ofr_numofr) CODI_OBRA, ofr_ttlobr OBRA, 
fges_OBRAEJECUTADA (ofr_idf_cod, ofr_emp_cod, ofr_numofr, 2) OBRA_EXECUTADA, 
ROUND((((TO_DATE(TO_CHAR(SYSDATE, 'DD/MM/YYYY'),'DD/MM/YYYY') - NVL(OFR_DATRPL001, NVL(OFR_DATCTR001, NVL(OFR_DATADJ, SYSDATE))))/30)/OFR_TEREXROFR)*100 ,2) TERMINI, 
nvl(fges_OBRAEJECUTADA (ofr_idf_cod, ofr_emp_cod, ofr_numofr, 2), 0) / 
decode(nvl(ROUND((((TO_DATE(TO_CHAR(SYSDATE, 'DD/MM/YYYY'),'DD/MM/YYYY') - NVL(OFR_DATRPL001, NVL(OFR_DATCTR001, NVL(OFR_DATADJ, SYSDATE))))/30)/OFR_TEREXROFR)*100 ,2), 1), 0, null, nvl(ROUND((((TO_DATE(TO_CHAR(SYSDATE, 'DD/MM/YYYY'),'DD/MM/YYYY') - NVL(OFR_DATRPL001, NVL(OFR_DATCTR001, NVL(OFR_DATADJ, SYSDATE))))/30)/OFR_TEREXROFR)*100 ,2), 1)) QUOCIENT, 
FGES_IMPORTPROJECTE (ofr_idf_cod, ofr_emp_cod, ofr_numofr, 2) FACTURAT 
--, FGES_IMPORTPROJECTE (ofr_idf_cod, ofr_emp_cod, ofr_numofr, 2, 1) COSTOS 
, ofr_ctaqlt CARPQUALITAT, ofr_dat FECFINALIZACION, FGES_ESTAT_PRJ(ofr_idf_cod, ofr_emp_cod, ofr_numofr) ESTADO, ofr_impadj IMPORTADJUDICACIO 
from tges_ofr, TRHU_OPE 
where OFR_IDF_COD = OPE_IDF_COD(+) 
AND OFR_OPE_CODCAO = OPE_COD(+) 
AND OFR_NUMOFR IS NOT NULL 
AND OFR_DATFIN IS NULL;


