CREATE OR REPLACE FORCE VIEW VGES_OFR
AS 
select
   decode(OFR_TIP, 0, 'Pblica', 1, 'Privada') AS TIPUS
 , OFR_SRO_COD as SERIE
 , OFR_ORD AS ORDRE
 , OFR_DATENT AS DATA_ENTRADA
 , OFR_TTLOBR AS TITOL
 , OFR_CODCLI AS CLIENT
 , OFR_NOMCLI AS NOM_CLI
 , OFR_CODSCL AS SUBCLI
 , OFR_TEL001 AS TELFON1
 , OFR_TEL002 AS TELFON2
 , OFR_EML AS EMAIL
 , OFR_FAX AS FAX
 , OFR_PERCON AS PERS_CONTACTE
 , OFR_DATPRS AS DATA_PRESENT
 , TO_CHAR(OFR_HORPRS, 'HH24:MI:SS') AS HORA_PRESENT
 , OFR_LLCPRS AS LLOC_PRESENT
 , OFR_DATPRXREV AS DATA_PROX_REV
 , OFR_TER AS TERM_PROJECTE
 , OFR_DATPBL AS DATA_PUBLIC
 , OFR_LLCPBL AS LLOC_PUBLIC
 , OFR_DIA AS DIES
 , decode(OFR_PRC, 1, 'Concurso', 2, 'Subasta', 3, 'Negociado', 4, 'Contrato menor') AS PROCEDIMENT
 , OFR_CLS AS CLASSIFICACI
 , OFR_AVA AS AVAL
 , OFR_IMP AS IMPORT
 , OFR_IMPIVA AS IMPORT_IVA
 , OFR_DATAPE AS DATA_APERTURA
 , TO_CHAR(OFR_HORAPE, 'HH24:MI:SS') AS HORA_APERTURA
 , decode(OFR_OFR, 'S', 'S', 'N', 'NO') AS CONFECCIONAR_OFERTA
 , decode(OFR_ARX, 'S', 'S', 'N', 'NO') AS ARXIVAR
 , OFR_NUMOFR AS NM_OFERTA
 , OFR_IMPOFR AS IMPORT_OFERTA
 , OFR_IMPOFRIVA AS IMPORT_OFERTA_IVA
 , OFR_DATOFR AS DATA_PRESENT_OFERTA
 , OFR_TEREXROFR AS TERMINI_EXEC_OFERTA
 , decode(OFR_PSTTEC, 'S', 'S', 'N', 'NO') AS PROPOSICI_TCNICA
 , decode(OFR_PSTECO, 'S', 'S', 'N', 'NO') AS PROPOSICI_ECONMICA
 , decode(OFR_DOCGRL, 'S', 'S', 'N', 'NO') AS DOC_GRAL
 , decode(OFR_ADJ, 'S', 'S', 'N', 'NO') AS ADJUDICADA
 , OFR_DATADJ AS DATA_ADJ
 , decode(OFR_CDC, 'S', 'S', 'N', 'NO') AS CADUCADA
 , OFR_DATCDC AS DATA_CAD
 , decode(OFR_FIN, 'S', 'S', 'N', 'NO') AS FINALITZADA
 , OFR_DATFIN AS DATA_FIN
 , decode(OFR_NONADJ, 'S', 'S', 'N', 'NO') AS NO_ADJUDICADA
 , OFR_DATNONADJ AS DATA_NOADJ
 , decode(OFR_NONPRS, 'S', 'S', 'N', 'NO') AS NO_PRESENTADA
 , OFR_DATNONPRS AS DATA_NOPRES
 , decode(OFR_ANU, 'S', 'S', 'N', 'NO') AS ANULADA
 , OFR_DATANU AS DATA_ANUL
 , decode(OFR_EXV, 'S', 'S', 'N', 'NO') AS EXTRAVIADA
 , OFR_DATEXV AS DATA_EXTR
 , decode(OFR_PEN, 'S', 'S', 'N', 'NO') AS PENDENT
 , OFR_DATPEN AS DATA_PEN
 , decode(OFR_ETD, 'S', 'S', 'N', 'NO') AS ESTUDI
 , OFR_DATETD AS DAT_EST
 , OFR_IMPADJ AS IMPORT_ADJUDI
 , OFR_IMPADJIVA AS IMPORT_ADJUDI_IVA
 , ofr_ctaqlt CARPETA
 , OFR_DATCTR001 AS DATA_CONTRACTE
 , OFR_DATRPL001 AS DATA_REPLANTEIG
 , OFR_DATATAREB AS DATA_ACTA_RECEP
 , ofr_dat as DATAFINAL
 , decode(ofr_senliq, 'S', 'S', 'N', 'No') as FALTALIQUIDACIO
 , OFR_ARXDOC ARXIU_DOC
 , OFR_LIQ LIQUIDADA
 , OFR_EMPADJ EMPRESA_ADJUDICACIO
 , OFR_IMPEMPADJ IMPORT_EMP_ADJUDICACIO
 , NVL((select sum(mof_impiva) from tges_mof where mof_idf_cod = ofr_idf_cod and mof_ofr_cod = ofr_cod and mof_tip='M'), 0) IMPMODIFICAT
 , NVL((select sum(mof_impiva) from tges_mof where mof_idf_cod = ofr_idf_cod and mof_ofr_cod = ofr_cod and mof_tip='C'), 0) IMPCOMPLEMENT
