 , NVL((select sum(mof_impiva) from tges_mof where mof_idf_cod = ofr_idf_cod and mof_ofr_cod = ofr_cod and mof_tip='A'), 0) IMPALTRES
 , (nvl(ofr_impadjiva, 0) + fges_sum_mof(ofr_idf_cod, ofr_cod)) IMPMODIFCOMPL
 , add_months(OFR_DAT,nvl(OFR_PGA001, 0))
 , add_months(OFR_DAT,nvl(OFR_PGA002, 0))
 , OFR_OPE_CODCGR  CAP_GRUP
 , OFR_TIPOBR   TIPOL_OBRA
