CREATE OR REPLACE FORCE VIEW VGES_ALBLIN
AS 
SELECT ALB.ALB_EMP_COD as EMPRESA, ALB.ALB_CLS CLASE,
 ALB.ALB_SER_COD SERIE,
 ALB.ALB_NUM NUMEROALBARAN,
 ALB.ALB_SER_COD || '/' || ALB.ALB_NUM as ALBARAN_CLIENTE,
 ALB.ALB_DIA FECHA, ALB.ALB_REF REFERENCIA,
 ALB.ALB_CLI_COD CLIENTE,
 (SELECT CLI_NOMCOM FROM TGES_CLI WHERE CLI_IDF_COD = ALB.ALB_IDF_COD AND CLI_COD = ALB.ALB_CLI_COD) NOMBRECLIENTE,
 ALB.ALB_PRJ_NUM PROYECTO,
 (SELECT PRJ_NOM FROM TGES_PRJ WHERE PRJ_IDF_COD = ALB.ALB_IDF_COD AND PRJ_EMP_COD = ALB.ALB_EMP_COD AND PRJ_NUM = ALB.ALB_PRJ_NUM) NOMBREPROYECTO,
 ALB.ALB_OPE_CODLLI CODENTREGA,
 (SELECT OPE_NOM FROM TRHU_OPE WHERE OPE_IDF_COD=ALB.ALB_IDF_COD AND OPE_COD = ALB.ALB_OPE_CODLLI) NOMENTREGA,
 ALB.ALB_TRA_COD CODTRASPORTISTA,
 (SELECT TRA_NOM FROM TGES_TRA WHERE TRA_IDF_COD = ALB.ALB_IDF_COD AND TRA_COD = ALB.ALB_TRA_COD) NOMTRASPORTISTA,
 ALB.ALB_MTR_COD MATRICULA,
 (SELECT MTR_DES FROM TGES_MTR WHERE MTR_IDF_COD = ALB.ALB_IDF_COD AND MTR_TRA_COD = ALB.ALB_TRA_COD AND MTR_COD = ALB.ALB_MTR_COD) NOMVEHICULO,
 LAC.LAC_ART_COD as ARTICULO,
 PGES_DES.ART (ALB.ALB_IDF_COD, lac.lac_art_cod) as ART_DESCRIP, LAC.LAC_DES as LINEA_DESCRIP,
 LAC.LAC_UNI  as UNIDADES, LAC.LAC_PRU  as PRECIO, LAC.LAC_DTO  as DESCUENTO,
 LAC.LAC_IMP  as IMPORTE, LAC.LAC_IVA_COD as IVA,
 LAC.LAC_LLA  as LARGO, LAC.LAC_AMP  as ANCHO, LAC.LAC_ALR  as ALTO, LAC.LAC_ORD_COD  as ORDEN_NUMERO,
 ALB.ALB_FPA FORMA_PAGO, ALB.ALB_FBL FACTURABLE,
 ALB.ALB_DOM DOMICILIO,
 ALB.ALB_OPE_CODCML COMERCIAL,
 (SELECT OPE_NOM FROM TRHU_OPE WHERE OPE_IDF_COD=ALB.ALB_IDF_COD AND OPE_COD = ALB.ALB_OPE_CODCML) NOMOPECOMERCIAL,
 ALB.ALB_OPE_CODPRP ENCARGADO,
 (SELECT OPE_NOM FROM TRHU_OPE WHERE OPE_IDF_COD=ALB.ALB_IDF_COD AND OPE_COD = ALB.ALB_OPE_CODPRP) NOMOPEENCARGADO,
 DECODE(alb.ALB_DTI, 'E', 'Entregado', 'R', 'Reservado', 'D', 'Destino') DESTINO,
 ALB.ALB_CPO_COD CODIGO_POSTAL,
 (SELECT CPO_POB FROM TGES_CPO WHERE CPO_IDF_COD = ALB.ALB_IDF_COD AND CPO_COD = ALB.ALB_CPO_COD) POBLACION,
 LAC.LAC_NUM  as NUMERO_LINEA, PGES_DES.IVA (ALB.ALB_IDF_COD, ALB.ALB_IVA_COD) as IVA_DESCRIP,
 LAC.LAC_FCS as FACTOR_CONVERSION_SALIDA, LAC.LAC_SEQ  as SECUENCIA, LAC.LAC_MOVSTO  as MOVIMIENTO_STOCK,
 LAC.LAC_LOT  as LOTE, LAC.LAC_DTO002  as DESCUENTO2, LAC.LAC_EVS_COD  as ENVASE, EVS_DES  as ENVASE_DESCRIP,
 LAC.LAC_UNIEVS  as ENVASE_UNI, LAC.LAC_PRUEVS  as ENVASE_PRECIO,
 ORD_DES  as ORDEN_DESCRIP, LAC.LAC_DIAFABCDC  as FECHA_FAB_CADUC, LAC.LAC_OBS  as OBSERVACIONES,
 LAC.LAC_RAP  as RAPPEL, LAC.LAC_TCS_PTE as PORCIENTO_COMISION, LAC.LAC_UNIMET  as UNIDADES_METRICAS,
 LAC.IMP_MATERIAL as IMP_MATERIAL, LAC.UNI_MOBRA as UNI_MANO_OBRA, LAC.IMP_MOBRA as IMP_MANO_OBRA,
 LAC.COST_MATERIAL as COSTE_MATERIAL, LAC.COST_MOBRA as COSTE_MANO_OBRA
 FROM VGES_CALCULS_LAC LAC, TGES_ALB ALB, TGES_EVS, TGES_ORD
 WHERE LAC.ALB_IDF_COD = ALB.ALB_IDF_COD AND LAC.ALB_EMP_COD = ALB.ALB_EMP_COD AND
  LAC.ALB_NUMDOC = ALB.ALB_NUMDOC AND LAC.ALB_IDF_COD = EVS_IDF_COD(+) AND
  LAC.LAC_EVS_COD = EVS_COD(+) AND LAC.ALB_IDF_COD = ORD_IDF_COD(+) AND
  LAC.ALB_EMP_COD = ORD_EMP_COD(+) AND LAC.LAC_PDT_NUMSER = ORD_PDT_NUMSER(+) AND
  LAC.LAC_ORD_COD = ORD_COD(+) and alb.alb_idf_cod = (SELECT PAR_VAL FROM TLIM_PARMETRES WHERE PAR_COD = 'IDF');


CREATE OR REPLACE FORCE VIEW VGES_ALBMOB
AS 
select idf,empresa,clase,serie,nalb, fechaalb, clicod, sum(impt_mo) as impt_mo, sum(horast) as horast ,sum(cost_mo) as cost_mo from
(
(
select idf,empresa,clase,serie,nalb, fechaalb,codigo_cliente as clicod, sum(importe) as impt_mo,0 as horast, 0 as cost_mo
from vges_albtfam
where tipo = 'O' --and importe <> 0
group by
idf,empresa,clase,serie,nalb, fechaalb, codigo_cliente
)
union all
(
select alb_idf_cod as idf, alb_emp_cod as empresa, alb_cls as clase, alb_ser_cod as serie , alb_num as numero, alb_dia as fechaalb, alb_cli_cod as clicod,0 as impt_mo,
sum(lff_hordec) as horest, sum( lff_cstuni * lff_hordec) as cost_mo
from tges_alb, trhu_lff, trhu_ffo
where
lff_idf_cod = alb_idf_cod and lff_emp_cod = alb_emp_cod and lff_lacnumdoc = alb_numdoc and ( lff_cstuni * lff_hordec <> 0 )
and ffo_idf_cod = lff_idf_cod and ffo_cod = lff_ffo_cod
and alb_fac_num is null --No facturat
group by
alb_idf_cod, alb_emp_cod, alb_cls, alb_ser_cod, alb_num, alb_dia, alb_cli_cod
)
)
group by idf,empresa,clase,serie,nalb, fechaalb,clicod;


CREATE OR REPLACE FORCE VIEW VGES_ALBOPECOS
AS 
select alb_idf_cod, alb_emp_cod, alb_cls, alb_ser_cod, alb_num, alb_dia,-- 0 as impt_mo, 0 as horast, 0 as cost_mo,
ffo_ope_cod, ope_nom, frhu_cat_ope(ope_idf_cod, ope_cod, alb_dia) as ope_cat, frhu_sec_ope(ope_idf_cod, ope_cod,'C', alb_dia, alb_emp_cod) as ope_sec,
sum( lff_cstuni * lff_hordec) as costmo, sum(lff_hordec) as hores
from tges_alb, trhu_lff, trhu_ffo, trhu_ope, trhu_cof
where
lff_idf_cod = alb_idf_cod and lff_emp_cod = alb_emp_cod and lff_lacnumdoc = alb_numdoc and ( lff_cstuni * lff_hordec ) <> 0
and ffo_idf_cod = lff_idf_cod and ffo_cod = lff_ffo_cod
and ope_idf_cod = ffo_idf_cod and ope_cod = ffo_ope_cod
and cof_idf_cod = lff_idf_cod and cof_cod = lff_cof_cod
and ( cof_tipuni = 1 or cof_uni = frhu_parametre(cof_idf_cod,'COFUNIHOR') )
and alb_fac_num is null --No facturat
group by alb_idf_cod, alb_emp_cod, alb_cls, alb_ser_cod, alb_num, alb_dia,ffo_ope_cod, ope_cod, ope_nom,ope_idf_cod;


