SELECT ALL idf, empresa, Codigo_Cliente, nombre_cliente, clase, FechaAlb, serie||'/'||Nalb Albaran,
 serie, nalb, far_cod codigo_familia, far_des nombre_familia,
 far_tip tipo,  ROUND(fges_canvi_divisa(idf, importe * pte * 0.01, div_cod, 'EURO', div_valeur, 1),2) importe,  codi_comercial
FROM tges_art, tges_far,
(
  SELECT ALL
  'ALBARAN' Tipo_alb,
  100 pte,
  alb_ser_cod serie,
  alb_num NAlb,
  alb_cls Clase,
  alb_cli_cod Codigo_Cliente,
  cli_nomfis nombre_cliente,
  alb_dia   FechaAlb,
  NVL(ela_art_cod, lac_art_cod) articulo,
  DECODE(ela_art_cod, NULL, NULL, lac_des) nombre_articulo,
  NVL(ela_unimet, 1) * lac_unimet unidades,
  NVL(ela_unimet * ela_pvp * lac_unimet * (1-nvl(lac_dto,0) *0.01) * (1- nvl(lac_dto002,0) *0.01), lac_imp) importe,
  alb_div_cod div_cod,
  alb_valdiveur div_valeur,
  cli_ope_cod codi_comercial,
  alb_idf_cod idf,
  alb_emp_cod empresa
 FROM tges_alb, tges_lac, tges_ela, tges_cli
 WHERE
   /* alb -< lac */
   lac_idf_cod = alb_idf_cod and
   lac_emp_cod = alb_emp_cod and
   lac_alb_numdoc = alb_numdoc and
   /* lac -< ela */
   ela_idf_cod (+) = lac_idf_cod and
   ela_emp_cod (+) = lac_emp_cod and
   ela_alb_numdoc (+) = lac_alb_numdoc and
   ela_lac_num (+) = lac_num and
   alb_idf_cod = cli_idf_cod and
   alb_cli_cod = cli_cod and
   /* Filtro */
   alb_fac_num is null --No facturat
)
WHERE
 art_cod  = articulo and
 far_idf_cod = art_idf_cod and
 far_cod = art_far_cod;


