  SUM(LAP_IMP) BASE,
  SUM(ROUND((LAP_IMP * IVA_PTE * 0.01),2) ) IVA,
  SUM(LAP_IMP+ ROUND((LAP_IMP * IVA_PTE * 0.01),2)) TOTAL
 FROM
  TGES_ALP,
  TGES_IVA,
  TGES_RPR,
  TGES_SCP,
  TGES_ACP,
  TGES_ALB,
  TGES_FPV,
  TGES_LAP,
  TGES_EMP
