  ALP_IDF_COD = (SELECT PAR_VAL FROM TLIM_PARMETRES WHERE PAR_COD = 'IDF') AND
  RPR_IDF_COD(+)=ALP_IDF_COD AND
  RPR_NUM(+)=ALP_RPR_NUM AND
  SCP_IDF_COD(+)=ALP_IDF_COD AND
  SCP_EMP_COD(+)=ALP_EMP_COD AND
  SCP_COD(+)=ALP_SCP_COD AND
  ACP_PRO_COD(+)=ALP_PRO_COD AND
  ACP_IDF_COD(+)=ALP_IDF_COD AND
  ACP_COD(+)=ALP_ACP_COD AND
  ALB_IDF_COD(+)=ALP_IDF_COD AND
  ALB_EMP_COD(+)=ALP_EMP_COD AND
