  ALP_NUMDOC  = LAP_ALP_NUMDOC AND
  LAP_IDF_COD = IVA_IDF_COD AND
  LAP_IVA_COD = IVA_COD AND
  LAP_IDF_COD = EMP_IDF_COD AND
  LAP_EMP_COD = EMP_COD
