CREATE OR REPLACE FORCE VIEW VGES_ANCPENFAC
AS 
SELECT
mdc_idf_cod,
mdc_emp_cod,
mdc_cxa_cod,
mdc_num,
mdc_div_cod,
mdc_cli_cod,
mdc_cls,
mdc_dia,
mdc_dpg_cod,
mdc_cnc,
mdc_pre_cod,
mdc_valdiveur,
MDC_IMP - NVL(SUM(ANF_IMP),0)  mdc_imp -- Importe pendiente de anticipar
FROM
tges_mdc,
tges_anf
WHERE
mdc_ser_cod is null and      --i que no tinguin fac ni ven associat.
mdc_fac_cls is null and
mdc_fac_num is null and
mdc_anc = 'S' and
mdc_idf_cod = anf_idf_cod(+) and      --Join amb desglose de anticipos
mdc_emp_cod = anf_emp_cod(+) and
mdc_cxa_cod = anf_cxa_cod(+) and
mdc_num     = anf_mdc_num(+)
GROUP BY
mdc_idf_cod,
mdc_emp_cod,
mdc_cxa_cod,
mdc_num,
mdc_div_cod,
mdc_cli_cod,
mdc_cls,
mdc_dia,
mdc_dpg_cod,
mdc_cnc,
mdc_pre_cod,
mdc_valdiveur,
MDC_IMP
HAVING
MDC_IMP - NVL(SUM(ANF_IMP),0) <> 0;


