CREATE OR REPLACE FORCE VIEW VGES_ANUPRJ
AS 
SELECT   /*+ RULE */ prj_num PROJECTE, prj_nom NOM_PROJECTE, cli_cod CLIENT, cli_nomcom NOM_CLIENT, ofr_ope_codcao CAPOBRA, ope_nom NOM_CAPOBRA, 
        (select max(fac_dia) from tges_fac where fac_idf_cod = prj_idf_cod and fac_emp_cod = prj_emp_cod and fac_prj_num = prj_num) DIADARRERAFAC, 
        round(( select sum(anu_imp-(anu_imp*(ofr_bai/100))) from tges_ofr ofr2, tges_anu where ofr2.ofr_idf_cod = ofr.ofr_idf_cod and ofr2.ofr_cod = ofr.ofr_cod and anu_idf_cod = ofr2.ofr_idf_cod and anu_ofr_cod = ofr2.ofr_cod), 2) ANUALITATS, 
        fges_ofr_fac(ofr_idf_cod, pre_emp_cod, 'EURO', prj_num, 2,0) FACTURAT, 
        round((select sum(anu_imp-(anu_imp*(ofr_bai/100))) from tges_ofr ofr2, tges_anu where ofr2.ofr_idf_cod = ofr.ofr_idf_cod and ofr2.ofr_cod = ofr.ofr_cod and anu_idf_cod = ofr2.ofr_idf_cod and anu_ofr_cod = ofr2.ofr_cod),2)-fges_ofr_fac(ofr_idf_cod,pre_emp_cod,'EURO',prj_num,2,0) RESTA 
  FROM  tges_pre, tges_prj, tges_pjp, tges_cli, trhu_ope, tges_ofr ofr 
  WHERE cli_idf_cod = prj_idf_cod and cli_cod = prj_cli_cod and pjp_idf_cod = pre_idf_cod 
        and pre_emp_cod = pjp_emp_cod and pre_cod = pjp_pre_cod and prj_num = pjp_prj_num 
		and prj_emp_cod = pjp_emp_cod and prj_idf_cod = pjp_idf_cod 
        and ope_idf_cod(+) = ofr_idf_cod and ope_cod(+) = ofr_ope_codcao 
		and ofr_numofr = pre_ser_cod || '/' || pre_num || '/' || pre_ver order by 4;


CREATE OR REPLACE FORCE VIEW VGES_APC
AS 
SELECT
 APC_IDF_COD, APC_EMP_COD, APC_PRE_COD, APC_NUM, APC_DIA, APC_IMP, APC_EST, APC_DES,
 APC_IMP - NVL(SUM(APP_IMP),0) APC_PEN
FROM tges_apc, tges_app
WHERE
 apc_idf_cod = app_idf_cod(+) AND
 apc_emp_cod = app_emp_cod(+) AND
 apc_pre_cod = app_pre_cod(+) AND
 apc_num = app_apc_num(+)
GROUP BY
 APC_IDF_COD,
 APC_EMP_COD,
 APC_PRE_COD,
 APC_NUM,
 APC_DIA,
 APC_IMP,
 APC_EST,
 APC_DES,
 APC_IMP;


CREATE OR REPLACE FORCE VIEW VGES_APPIVA
AS 
SELECT
 APP_IDF_COD, APP_EMP_COD, APP_PRE_COD, APP_APC_NUM, APP_NUM,
 APP_IMP, APP_IVA_COD, APP_PRE_CODCER, APP_CER_NUM, APP_ALB_NUMDOC, APP_LAC_NUM,
-- ROUND(APP_IMP / (1 + DECODE(pre_cls, 1, 0, DECODE(emp_tipfac, 2, 0, IVA_PTE )) /100), div_decimp) APP_IMPSENIVA
ROUND(APP_IMP / ((1-nvl(pre_dte,0)*0.01) *(1-nvl(pre_dte002,0)*0.01)) / (1 + DECODE(pre_cls, 1, 0, DECODE(emp_tipfac, 2, 0, IVA_PTE )) /100), div_decimp) APP_IMPSENIVA
FROM
 tges_app, tges_emp, tges_pre, tges_iva, tges_div
WHERE
 /* app >- pre */
 app_idf_cod = pre_idf_cod AND
 app_emp_cod = pre_emp_cod AND
 app_pre_cod = pre_cod AND
 /* pre >- div */
 pre_idf_cod = div_idf_cod AND
 pre_div_cod = div_cod AND
 /* app >- iva */
 app_idf_cod = iva_idf_cod AND
 app_iva_cod = iva_cod AND
 /* app >- emp */
 app_idf_cod = emp_idf_cod AND
 app_emp_cod = emp_cod;


