BFC_EMP_COD, 
BFC_SER_COD, 
BFC_FAC_NUM, 
BFC_FAC_CLS, 
BFC_IVA_COD, FAC_CLI_COD, 
SUM(LAC_IMP) BRUT, 
BFC_BIM, 
IVA_PTE, 
bfc_bim * iva_pte / 100 IMP_IVA, 
DECODE(cli_recequ , 'S' , IVA_REQ , 0) IVA_REQ, 
bfc_bim * (DECODE(cli_recequ , 'S' , IVA_REQ , 0)) / 100 IMP_REQ, 
bfc_bim+ (bfc_bim * iva_pte / 100) + (bfc_bim * (DECODE(cli_recequ , 'S' , IVA_REQ , 0)) / 100) TOTAL, 
decode(FAC_PTERET,NULL,NULL,DECODE(fac_tipret,'1' ,Round( fac_bim * fac_pteret * 0.01, 2 ),'2', 
                       Round( ( fac_bim + fac_quaiva ) * fac_pteret * 0.01, 2 ))) RET_TOT 
--					   fac_bim + fac_quaiva - imp_ret; 
FROM TGES_BFC, TGES_IVA, TGES_FAC, TGES_ALB, TGES_LAC, TGES_CLI 
WHERE BFC_IDF_COD = IVA_IDF_COD 
  AND BFC_IVA_COD = IVA_COD 
  -- 
  AND BFC_IDF_COD = FAC_IDF_COD 
  AND BFC_EMP_COD = FAC_EMP_COD 
