CREATE OR REPLACE FORCE VIEW VGES_CMGUNIPEN
AS 
select cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod, lmg_num,lmg_uni, nvl(sum(lac_uni),0)  lmg_uniser
from tges_cmg, tges_lmg, tges_lac
where cmg_est = 1
and lmg_idf_cod = cmg_idf_cod and lmg_emp_cod = cmg_emp_cod and lmg_cmg_num = cmg_num
and lac_idf_cod(+) = lmg_idf_cod and lac_emp_cod(+) = lmg_emp_cod and lac_cmg_num(+) = lmg_cmg_num
and lac_lmg_num(+) = lmg_num and lac_art_cod(+) = lmg_art_cod
group by cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod,lmg_num,lmg_uni
having ( lmg_uni - nvl(sum(lac_uni),0) ) > 0
order by cmg_idf_cod,cmg_mag_cod,cmg_emp_cod,cmg_num,lmg_art_cod;


CREATE OR REPLACE FORCE VIEW VGES_CMPALBTPV
AS 
select IMP_IDF_COD, IMP_EMP_COD, IMP_SER_COD, IMP_ALBNUM, IMP_DIA, IMP_TPV_COD, sum(nvl(Imptpv,0)) Imptpv,  sum(nvl(ImpGes,0)) ImpGes 
from ( 
  select IMP_IDF_COD, IMP_EMP_COD, IMP_SER_COD, IMP_ALBNUM, IMP_DIA, IMP_TPV_COD, IMP_IMP Imptpv, 0 ImpGes  from tges_imp_tpv 
  union all 
  select fac_idf_cod alb_idf_cod, fac_emp_cod alb_emp_cod, fac_ser_cod alb_ser_cod, fac_num alb_num, fac_dia alb_dia, 
  (select min(alb_ptv_cod) from tges_alb where alb_idf_cod = fac_idf_cod and alb_emp_cod = fac_emp_cod and alb_ser_codfac = fac_ser_cod and alb_fac_cls = fac_cls and alb_fac_num = fac_num) alb_ptv_cod, 
  0 Imptpv, 
  fges_impfactot (fac_idf_cod, fac_emp_cod, fac_ser_cod, fac_cls, fac_num) ImpGes 
  from tges_fac 
) 
group by IMP_IDF_COD, IMP_EMP_COD, IMP_SER_COD, IMP_ALBNUM, IMP_DIA, IMP_TPV_COD 
having sum(nvl(imptpv,0)) <> sum(nvl(impges,0));


