CREATE OR REPLACE FORCE VIEW VGES_COPPENREB
AS 
select 
cop_idf_cod,cop_emp_cod,cop_prj_num,prj_nom,cop_dia,cop_pro_cod, to_char(cop_dia,'YYYY') as cop_any,to_char(cop_dia,'MM') as cop_mes, 
lcm_ref,lcm_cop_num,lcm_num,lcm_art_cod,lcm_unimet,lcm_unimetgtt, 
nvl(lcm_uni - nvl(sum(lap_uni),0),0) as uni_PDT, 
nvl(lcm_unigtt - nvl(sum(lap_unigtt),0),0) as unigtt_PDT, 
lcm_fce,lcm_pru,lcm_dto,lcm_dtoppp,lcm_amp,lcm_lla,lcm_alr, 
NVL(lcm_unimet - nvl(sum(lap_unimet),0),0) as unimet_pdt, 
NVL(lcm_unimetgtt -nvl(sum(lap_unimetgtt),0),0) as unimetgtt_pdt, 
lcm_des,nvl(prj_div_cod,cop_div_cod) as div_cod, 
fges_canvi_divisa(cop_idf_cod,NVL(lcm_unimet - nvl(sum(lap_unimet),0),0) * nvl(lcm_pru,0) * ( 1 - 0.01 * nvl(lcm_dto,0)) * ( 1 - 0.01 * nvl(lcm_dtoppp,0)),cop_div_cod,nvl(prj_div_cod,cop_div_cod)) as imp_pdt 
from tges_lcm, tges_lap, tges_cop, tges_prj 
where 
lcm_idf_cod = lap_idf_cod(+) 
and lcm_emp_cod = lap_emp_cod(+) 
and lcm_cop_num = lap_cop_cod(+) 
and lcm_num = lap_lcm_num(+) 
and lcm_art_cod = lap_art_cod(+) 
and lcm_idf_cod = cop_idf_cod 
and lcm_emp_cod = cop_emp_cod 
and lcm_cop_num = cop_num 
and cop_est = '2' 
and cop_idf_cod = prj_idf_cod(+) 
and cop_emp_cod = prj_emp_cod(+) 
and cop_prj_num = prj_num(+) 
